@-moz-document url("https://unity.com/advisorypanel"), domain("unity3d.com"), domain("certification.unity.com"), domain("store.unity.com"), domain("pay.unity.com"), domain("id.unity.com"), domain("api.unity.com"), regexp("unity.com/*"), regexp("file://.*Unity.*/Documentation/.*") {
	/*********************************************************
	 * ORIGINAL THEME CREDITS: 
	 * Company: Unity Technologies
	 * Author:  Rickard Andersson, rickard@unity3d.com
	 * DARK THEME CREDITS:
	 * Author: Kamran Mackey <kamranm1200@gmail.com>
	 * Version: 1.1.2 (Nitrogen)
	 * Last update: December 17, 2018
	*********************************************************/
	
	/**********************************
	*
	* GENERAL UNITY CSS
	*
	**********************************/
	
	body {
		background: #2b2b2b;
		color: #fff;
	}
	
	h1, h2, h3, h4, h5 {
		color: #fff;
	}
	
	.expand, .expand:hover, .expand:active, .expand:focus {
		color: #fff;
		transform: expanded;
	}
	
	.br {
		border-right-color: #424242;
	}
	
	div.crumbs ul li {
		color: #d9d9d9;
	}
	
	.lessons-list ol li.current {
		color: #dfdfdf
	}
	
	.lessons-list ol li a {
		color: #aeaeae
	}
	
	.bl {
		border-left-color: #424242;
	}
	
	header.main-header {
		background: transparent;
	}
	
	div.main-navigation:before {
		background: transparent;
	}
	
	nav.sub-nav:before, nav.sub-nav:after {
		background: rgba(145, 145, 145, 0.1);
	}
	
	div.block {
		background: #2b2b2b;
	}
	
	ul li:before {
		color: #fff;
	}
	
	div.block-footer {
		background: #2b2b2b;
		border-top: #2b2b2b 1px solid;
	}
	
	.main-navigation .menu .expanded .menu {
		background: rgba(123, 123, 123, 0.9);
	}
	
	.fancybox-skin {
		background: #424242;
		color: #fff;
	}
	
	/**
	 * Use the white Unity logo on all pages.
	 *
	 * This is a hacky way to do this as the URL to this image
	 * can change at any time. But if it does, I'll try my best 
	 * to fix the broken URL.
	 */
	header.main-header a.unity-logo {
		background-image: url('https://unity3d.com/profiles/unity3d/themes/unity/images/ui/ui/unity-logo-white.svg');
	}
	
	::placeholder {
		color: #c8c8c8;
	}
	
	div.select-box ul.options li a, div.select-box ul.options li a:hover, 
	div.select-box ul.options li a:active, div.select-box ul.options li a:focus {
		color: #f3f3f3;
	}
	
	div.select-box ul.options {
		box-shadow: 0 0 35px #1f2022;
		background-color: #2f2f2f;
	}
	
	div.select-box ul.options a:hover {
		background-color: #333;
	}
	
	.bg-light-grey {
		background: #2b2b2b;
	}
	
	/**********************************
	 *
	 * UNITY LEARN
	 *
	***********************************/
	
	.card-content {
		border-color: #424242;
	}
	
	/**********************************
	 *
	 * UNITY DOWNLOAD ARCHIVE
	 *
	***********************************/
	
	.ba {
		border-color: #424242;
	}
	
	ul.subtabs li a {
		transition: ease-out .30s;
		color: #808080;
	}
	
	ul.subtabs li a:hover {
		transition: color ease-in .30s;
		color: #00bcd4;
	}
	
	ul.subtabs {
		border-bottom-color: #424242;
	}
	
	.unity-download .archive .row {
		transition: ease-out .15s;
	}
	
	.unity-download .archive .row:hover {
		background: #424242;
		transition: ease-in .15s;
	}
	
	/**********************************
	 *
	 * UNITY BETA PROGRAM
	 *
	***********************************/
	
	nav.tertiary-nav {
		background: #2b2b2b;
	}
	
	nav.tertiary-nav div.wrap ul.tabbed li a {
		color: #fff;
	}
	
	nav.tertiary-nav div.wrap ul.tabbed li {
		border-right-color: #424242;
	}
	
	nav.tertiary-nav div.wrap ul.tabbed li:first-child {
		border-left-color: #424242;
	}
	
	/**********************************
	 *
	 * UNITY PRODUCT PAGE SPECIFIC CSS
	 *
	**********************************/
	
	.bb {
		border-bottom-color: #919191;
	}
	
	.bt {
		border-top-color: #919191;
	}
	
	.products-grid--right {
		border-left-color: #919191;
	}
	
	.products-grid--item {
		border-right-color: #919191;
		border-bottom-color: #919191;
	}
	
	/**********************************
	 *
	 * UNITY PUBLIC RELATIONS CSS
	 *
	**********************************/
	.bg-mg {
		background-color: #2b2b2b !important;
	}
	
	.growth-card {
		background-color: #424242;
	}
	
	.growth-card h3,
	.growth-card h4 {
		color: #f2f2f2;
	}
	
	/**********************************
	 *
	 * UNITY PRESS RELEASES
	 *
	**********************************/
	.node-type-unity-news h1, .node-type-unity-news h2, 
	.node-type-unity-news h3 {
		color: #fff;
	}
	
	/**********************************
	 *
	 * UNITY FEEDBACK
	 *
	**********************************/
	div.pagination a.next_page {
		background-position: 7px -113px;
	}
	
	div.pagination a.next_page {
		text-indent: -9999em;
		background: #424242 url(https://unity3d.com/profiles/unity3d/themes/unity/images/assets/layout/core-sprites.png) -39px -113px no-repeat;
		border-color: #424242;
		transition: ease-in .15s;
		transform: rotate(180deg);
	}
	
	div.pagination a.previous_page {
		text-indent: -9999em;
		border-color: #424242;
		background: #424242 url(https://unity3d.com/profiles/unity3d/themes/unity/images/assets/layout/core-sprites.png) -39px -113px no-repeat;
		transition: ease-in .15s;
	}
	
	div.pagination a.previous_page:hover, 
	div.pagination a.next_page:hover {
		background: #333 url(https://unity3d.com/profiles/unity3d/themes/unity/images/assets/layout/core-sprites.png) -39px -113px no-repeat;
		transition: ease-out .15s;
		border-color: #fff;
	}
	
	div.pagination a {
		color: #fff;
		transition: ease-in .15s;
		border-color: #424242;
	}
	
	div.pagination a:hover {
		color: #fff;
		transition: ease-out .15s;
	}
	
	.cn {
		color: #fff !important;
	}
	
	.cn:hover {
		color: #808080 !important;
	}
	
	.bg-ud {
		background: #2b2b2b !important;
	}
	
	/**********************************
	 *
	 * UNITY ID
	 *
	**********************************/
	header.main-header a.user-icon > i {
		color: #fff;
	}
	
	header.main-header a.app-name, header.main-header a.app-name:hover {
		color: #fff
	}
	
	div.user-head .profile-name {
		color: #fff;
	}
	
	div.user-head .profile-email {
		color: #c8c8c8;
	}
	
	.dashboard div.header-panel {
		border-top-color: #424242;
	}
	
	div.user-head .orgs {
		border-top-color: #424242;
	}
	
	div.user-head .user-panel-item {
		color: rgba(255,255,255,0.8);
		border-top-color: #424242;
	}
	
	div.user-head .user-panel-item:hover {
		background-color: #424242;
	}
	
	div.user-head .orgs .org-items > a:hover, 
	div.user-head .orgs .org-items > a.on {
		background-color: #424242;
	}
	
	div.user-head .user-panel-item:last-child {
		border-bottom-color: #424242;
	}
	
	div.user-head .orgs .org-items > a, div.user-head .orgs .org-items > div {
		color: rgba(255, 255, 255, 0.8);
	}
	
	.account_settings .item-box {
		color: #fff;
	}
	
	.item-list div.item-title {
		color: #fff;
	}
	
	div.sidebar-navigation ul li a {
		color: #fff;
	}
	
	div.sidebar-navigation ul li ul {
		background: #424242;
	}
	
	div.sidebar-navigation ul li ul:before {
		border-bottom-color: #424242;
	}
	
	.action-ico:before {
		color: #fff;
	}
	
	.bg-lg {
		background-color: #2b2b2b !important;
	}
	
	.no-touch div.item-list .item:hover:before {
		-webkit-box-shadow: 0 0 0 4px #424242;
		-moz-box-shadow: 0 0 0 4px #424242;
		box-shadow: 0 0 0 4px #424242;
	}
	
	div.block-header {
		border-bottom: #424242 1px solid;
		background: #2b2b2b !important;
	}
	
	div.item-list .item div.lbl {
		color: #fff;
	}
	
	div.item-list .item {
		background: #2b2b2b !important;
	}
	
	/** Authentication borders **/
	.authentication p.or:before, .authentication p.or:after,
	.authentication .line {
		background-color: #545353;
	}
	
	.security .block-header, .security .block {
		background-color: #424242 !important;
	}
	
	div.form-field-helper {
		background: #919191;
		border-bottom: #2196f3 5px solid;
	}
	
	/**********************************
	*
	* UNITY EDUCATION
	*
	**********************************/
	.component-card {
		background: #424242;
		color: #fff;
	}
	
	.bg-white {
		background: #2b2b2b;
	}
	
	/**********************************
	*
	* UNITY CERTIFICATION
	*
	***********************************/
	.bg-wh {
		background: #2b2b2b;
	}
	
	/**********************************
	*
	* UNITY STORE
	*
	**********************************/
	div.header-panel {
		background: #2b2b2b;
	}
	
	/* Use the white version of the Unity avatar image
	 * for better visibility.
	 */
	div.user-panel img.avatar {
		display: block;
		-moz-box-sizing: border-box;
		box-sizing: border-box;
		/** This is very hacky as this URL could change at any time **/
		background: url(https://store.unity.com/themes/contrib/unity_base/images/icons/other/user-default-light128x128.png) no-repeat;
		width: 128px;
		height: 128px;
		padding-left: 128px;
		margin: 0 auto 20px auto;
		border-radius: 50%;
	}
	
	.c-db {
		color: #fff;
	}
	
	.c-dg {
		color: #b5b5b5 !important;
	}
	
	.products .sticky-nav {
		background-color: #2b2b2b;
	}
	
	.products .sticky-nav li.logo,
	.products .sticky-nav li a,
	.section-selector h1,
	.products .section-leading-unit a,
	.products .section-copy-block h2,
	.products .section-copy-block h3,
	.products .section-copy-block ul li,
	.section-plans .plans-callout h4,
	.section-faq .section-subheadline,
	.section-faq .question-wrapper p,
	.section-faq .question-wrapper span,
	.section-plans .product-tagline, .result .product-tagline,
	.section-plans .container-cards .section-headline-links,
	.section-plans .caption-copy, .result .caption-copy,
	.section-plans .price-container .prices, .result .price-container .prices,
	.section-plans .cards-bottom-list .li-headline, .result .cards-bottom-list .li-headline .section-plans .cards-bottom-list li, .result .cards-bottom-list li,
	.section-plans .cards-bottom-list li, .result .cards-bottom-list li,
	.section-plans .cards-bottom-list li::before, .result .cards-bottom-list li::before,
	.section-plans .show-benefits p,
	.section-plans .eligibility p {
		color: #fff;
	}
	
	.products .sticky-nav li a:hover {
		color: #26c6da;
	}
	
	.products .section-leading-unit--cards__card {
		box-shadow: 0 0 25px #1f2022
	}
	.products .section-leading-unit--cards__card:hover {
		box-shadow: 0 0 35px #17181a;
	}
	
	.products .section-testimonial .block-quote,
	.products .section-testimonial .block-quote-flex {
		background-color: #2b2b2b;
	}
	
	.section-faq.front-page-faq,
	.section-faq .container-faq,
	.products .section-faq.other {
		background-color: #2b2b2b;
	}
	
	.section-plans .product-info-pro small, .result .product-info-pro small {
		color: #fff;
	}
	
	.section-plans .product-info-pro, .result .product-info-pro {
		background: #2b2b2b;
		border: 2px solid #808080;
		width: 100%;
		text-align: center;
		margin: 0 auto;
		margin-bottom: 25px;
	}
	
	div.store-footer:before,
	.store-footer,
	.store-footer p {
		color: #fff;
		background-color: #2b2b2b;
	}
	
	.products.unity-products p {
		color: #fff;
	}
	
	.section-plans .card-unity-personal .plan-benefits {
		border: #808080 1px solid;
		background-color: #2b2b2b;
	}
	
	.products .section-benefits,
	.section-faq .container-resources,
	.products .section-faq,
	.products .section-faq .question-wrapper p,
	.products .section-faq .question-wrapper span {
		color: #fff;
		background-color: #2b2b2b;
	}
	
	.section-plans .container-cards .duration, .result .container-cards .duration {
		color: #808080;
	}
	
	.section-plans .cards, .result .cards {
		background-color: #2b2b2b;
		box-shadow: 0 0 25px #1a1b1c;
	}
	
	.section-industries {
		background: #2b2b2b;
	}
	
	.section-industries h2 {
		color: #fff;
	}
	
	/**********************************
	 *
	 * UNITY STORE PREMIUM SUPPORT
	 *
	**********************************/
	div.gradient-diamond {
		border-right-color: #2b2b2b;
		border-left-color: #2b2b2b;
		border-bottom-color: #2b2b2b;
	}
	
	div.gradient-diamond:before, div.gradient-diamond:after {
		background: #2b2b2b;
	}
	
	.bg-db {
		background-color: #2b2b2b;
	}
	
	/**********************************
	 *
	 * UNITY STORE COMPARE PLANS
	 *
	 **********************************/
	.page__title,
	.page__subtitle {
		color: #fff;
	}
	
	.compare-plans-table__cell {
		border: #808080 1px solid;
	}
	
	.compare-plans-table__cell--is-sticky::before {
		border-color: #808080;
	}
	
	.compare-plans-table__cell--is-sticky {
		background: #2b2b2b;
	}
	
	.compare-plans-table__text {
		color: #eeecec;
	}
	
	.plan__target-audience,
	.plan__title,
	.plan__price,
	.plan__text {
		color: #fff;
	}
	
	/**********************************
	 *
	 * UNITY BLOG SPECIFIC CSS
	 *
	**********************************/
	
	div.large p, div.medium p, div.list-post p {
		color: #f2f2f2;
	}
	
	div.popular-posts div.list-post-img {
		background: linear-gradient(to bottom, #47aece, #5cd2f4);
	}
	
	.medium-content {
		background: linear-gradient(to bottom, rgba(66, 66, 66, 0.9), rgba(66, 66, 66, 1) 60%);
	}
	
	div.paging ul li .current {
		color: #f2f2f2;
	}
	
	div.paging ul li a {
		color: #cacaca;
	}
	
	div.paging ul li a:hover {
		color: #f2f2f2;
	}
	
	.post-heading {
		color: #f2f2f2 !important;
	}
	
	.post-heading a:visited {
		color: #dbdbdb !important;
	}
	
	.bt {
		border-top-color: #424242;
	}
	
	.bb {
		border-bottom-color: #424242;
	}
	
	.slick-dots li button:hover, .slick-dots li button:focus, .slick-dots li.slick-active button, .slick-dots li.slick-active button {
		background: #47b2bd;
		border-color: #47b2bd;
	}
	
	.slick-prev, .slick-next {
		border-color: #47b2bd;
	}
	
	.slick-prev:before, .slick-next:before {
		color: #47b2bd;
	}
	
	.list-post-author, .post-author {
		color: #f2f2f2;
	}
	
	.sidebar-column .categories .category .name {
		border-left-color: #424242;
		color: #f2f2f2;
	}
	
	a.category-tag {
		border: 1px solid #d6d6d6 !important;
		color: #f2f2f2 !important;
	}
	
	.bg-ye.light {
		background-color: #2b2b2b !important;
	}
	
	.post-heading a {
		color: #f2f2f2 !important;
	}
	
	.post-author a {
		color: #f2f2f2;
	}
	
	div.post-full div.content ul li:before {
		color: #f2f2f2;
	}
	
	div.post-full div.content ul li {
		color: #f2f2f2;
	}
	
	div.post-full p, div.post-full div.content table td {
		color: #f2f2f2;
	}
	
	/** Comments **/
	.comments ol li ol .byuser > div.comment:first-child {
		background: #388b94;
	}
	
	/**********************************
	 *
	 * UNITY DEVELOPER
	 *
	**********************************/
	md-card {
		background-color: rgba(43, 43, 43, 1)
	}
	
	/* .project-header .project-headline {
		color: rgba(255, 255, 255, .7);
	} */
	md-button {
		color: rgba(255, 255, 255, 0.7);
	}
	
	md-icon {
		color: rgba(255, 255, 255, 0.7);
	}
	
	md-select .md-select-icon {
		color: rgba(255, 255, 255, 0.7)
	}
	
	md-select.md-default-theme .md-select-value.md-select-placeholder, md-select .md-select-value.md-select-placeholder {
		color: rgba(255, 255, 255, 0.7);
	}
	
	/**********************************
	 *
	 * UNITY DOCUMENTATION
	 *
	**********************************/
	
	div.header .more .filler {
		background: #424242;
	}
	
	.mCSB_scrollTools .mCSB_draggerRail {
		background: #424242;
	}
	
	.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar {
		background: #bbbbbb;
	}
	
	div.toolbar div.script-lang div.dialog-content {
		border-top-color: #424242;
		background: #424242;
	}
	
	div.toolbar div.script-lang div.dialog-content p {
		border-top-color: #808080;
	}
	
	div.toolbar div.script-lang ul li {
		background: #424242;
	}
	
	div.toolbar div.script-lang ul li:hover {
		background: #808080;
	}
	
	div.toolbar div.script-lang ul li.selected {
		background: #666;
	}
	
	div.toolbar div.script-lang div.dialog:before {
		border-bottom-color: #424242;
	}
	
	div.header .more ul li a {
		color: #fff;
		transition: ease-out .15s;
	}
	
	div.header .more ul li a:hover {
		color: #31daec;
		transition: ease-in .15s;
	}
	
	div.version-number {
		background: #2b2b2b;
	}
	
	div.message-error {
		background: #a00303;
	}
	
	div.subsection div.subsection h2 {
		color: #e3e3e3;
	}
	
	code {
		background: transparent;
	}
	
	pre code {
		background: #424242;
		color: #fff;
	}
	
	.gray-btn {
		background: #424242;
	}
	
	.gray-btn:hover {
		background: #808080;
	}
	
	.otherversionscontent {
		background-color: #424242;
	}
	
	.otherversionscontent a {
		color: #fff;
	}
	
	.nextprev {
		background: #424242;
	}
	
	.content .section a {
		text-decoration: underline;
		color: #fff;
		transition: ease-out .15s;
	}
	
	.content .section a:hover {
		text-decoration: underline;
		color: #31daec;
		transition: ease-in .15s;
	}
	
	.obsolete-version-number a,
	.version-number a {
		text-decoration: underline;
		color: #dbdbdb;
		transition: ease-out .15s;
	}
	
	.obsolete-version-number a:hover,
	.version-number a:hover {
		text-decoration: underline;
		color: #31daec;
		transition: ease-in .15s;
	}
	
	.tooltip .tooltiptext {
		background: #424242;
		color: #fff;
	}
	
	.tooltip .tooltiptext a {
		transition: none;
	}
	
	.content .section table thead th {
		background: #3e3e3e;
		border-color: #606060;
	}
	
	.content .section table tbody td {
		background: #373737;
		border-color: #606060;
	}
	
	/**
	 * WARNING: Backwards compatibility only!
	 *
	 * These lines of CSS exist solely for the purpose of maintaining
	 * backwards compatibility with Unity documentation versions 5.0.x
	 * to 5.2.x. Newer versions of Unity's documentation lack the history
	 * table, most likely due to the Unity API having grown so large since 
	 * 5.2.x. Once Unity Technologies removes the older documentation from 
	 * their site, these lines will be removed, unless other uses of these
	 * lines are discovered which warrant keeping this around.
	 **/
	.history-table th {
		background: #424242;
	}
	
	.history-table td {
		border-bottom-color: #424242;
	}
	
	div.sidebar-menu ul li a {
		color: #dfdfdf;
	}
	
	div.lang-switcher div.current div.arrow {
		filter: invert(100%);
	}
	
	div.search-results h2 {
		border-bottom-color: #424242;
	}
	
	div.sidebar p {
		background: #2b2b2b;
	}
	
	div.sidebar-menu ul li a:hover {
		color: #ececec;
	}
	
	div.sidebar-menu ul li a.current {
		background: transparent;
		color: #fff;
	}
	
	div.sidebar-menu ul li div.collapsed {
		background-color: #424242;
		border: #424242 1px solid;
	}
	
	div.sidebar-menu ul li div.arrow {
		background-color: #2b2b2b;
		border: #2b2b2b 1px solid;
	}
	
