css:
@-moz-document domain("educus.nl") {
.blocks,
.now--figure {
    display: none !important;
}
header.header,
.header-toolbar {
    box-shadow: none !important;
    background: transparent !important;
    position: absolute;
    right: 0;
}
header.header {
    left: 0;
}
.header-tabs {
    box-shadow: none;
    height: auto;
}
.header-tabs a {
    padding-left: 20px;
    padding-right: 20px;
}

.content l-medium {
    padding-top: 0 !important;
}

.navigation-items,
.navigation-footer {
    background-color: #2e2e2e;
}

.navigation-header {
    display: none;
}

.content-wrapper,
body {
    background-color: #3d3d3d;
    color: #fafafa;
}

.now--wrapper h1 {
    padding-top: 0 !important;
    margin-top: 0 !important;
}

.now {
    background-color: #2e2e2e !important;
    color: #efefef !important;
    border: 0;
}

.popover-tabs ul a,
.header-tabs a {
    background-color: #2e2e2e;
    color: #efefef;
    border: none !important;
}

.now > a > div > h4,
li.now--tomorrow div,
.presence-total,
.student-absence--course,
div.overzicht table.table td {
    background-color: #2e2e2e !important;
    color: #efefef !important;
}

span.student-absence--hour-title,
.student-absence--day-title {
    background: #272727 !important;
}

ul.navigation-items--main > li.is-selected > a,
ul.navigation-items--main > li.is-selected > a i.flaticon:before {
    color: #efefef;
}

.now--body > h4 {
    color: #efefef;
}

strong {
    color: #a2a2a2 !important;
}

.now--tomorrow {
    border: none !important;
}

.agenda-filter,
.time-filter {
    top: 0;
    background-color: #2e2e2e;
}

input.hasDatepicker,
.popover--field > select,
.popover--field > input {
    background: #404040;
    color: #f2f2f2 !important;
}

ul.navigation-items--main > li > a {
    color: #989898;
}

.popover-tabs li.is-selected a,
.header-tabs li.is-selected a {
    background-color: #4d4d4d;
    color: white;
}

.header-tabs a:hover {
    background-color: #4d4d4d;
    color: white;
}

.agenda-list tbody tr {
    background-color: #2e2e2e;
}

.agenda-list td span {
    color: #a2a2a2 !important;
}

.agenda-list td span.agenda-class {
    color: #efefef !important;
}

.agenda-time.top,
i.flaticon.is-completed {
    color: #d6d6d6 !important;
}

i.flaticon.is-completed {
    opacity: .25;
}

td.agenda-item {
    width: 6px;
    background-color: #2b2b2b !important;
}

td.agenda-item.is-active {
    background-color: #4c4c4c !important;
}

hr {
    border-color: #5a5a5a;
    margin: 2em 4em;
}

.header-toolbar h1 {
    text-align: right;
    display: none;
    padding: 0 1.5rem;
}

.header-toolbar .header-toolbar--action {
    background-color: transparent;
}

.header-toolbar .header-toolbar--action > i,
.header-toolbar .header-toolbar--action > span {
    margin-right: 20px;
}

.is-fixed .is-lastcrumb .is-single {
    width: 100% !important;
}

.header-tabs {
    position: absolute;
    top: 0;
    left: 0;
}

.l-flex-content {
    position: relative;
}

.popover {
    background-color: #2e2e2e;
    padding: 12px;
}

.popover-title {
    margin: 20px 0 !important;
}

.popover-title > h2 {
    padding: 0;
    margin: 0 !important;
}

.popover-content > form > fieldset > .popover--field {
    margin: 14px 0;
}

.popover--field {
    border-bottom: 2px solid #3c3c3c;
}

div.overzicht.no-padding table.table td,
.panel {
    background: #2e2e2e !important;
    color: #f2f2f2 !important;
    padding: 10px 20px;
    border-bottom: 2px solid #3c3c3c !important;
}

div.task--summary,
.popout > * {
    background: #2e2e2e !important;
    color: #f2f2f2 !important;
    border-bottom: 2px solid #3c3c3c !important;
}

div.overzicht table.table td {
    border-bottom: 2px solid #3c3c3c !important;
}

tr {
    margin-bottom: 20px;
}
}
