css:
@-moz-document regexp("https?://cmake.org/cmake/help.*") {
div.document {
    background-color: #444;
}

div.body {
    background-color: #222;
    color: #ddd;
}

div.body h1,
div.body h2,
div.body h3,
div.body h4,
div.body h5,
div.body h6 {
    color: #6095F4;
}

div.note{
    background-color: #111
}

span.highlighted {
    color: red;
    background-color: #fd0;
}

a {
    color: #0b83FF;
    text-decoration: underline;
}

pre,
code,
.note code {
    color: #fcc;
}

.literal-block a.reference.internal,
div.body h1,
div.body h2,
div.body h3,
div.body h4,
div.body h5,
div.body h6,
pre,
code,
.note code,
div.contents  {
    background-color: #292929;
}

div.sphinxsidebarwrapper h3,
div.sphinxsidebarwrapper *,
div.sphinxsidebarwrapper h4,
div.sphinxsidebarwrapper h3 a {
    color: #eeeeee;
}

/* syntax highlight */
.highlight .nb{ /*string literal*/
    color: #aba
}
.highlight .m{ /*number literal*/
    color: #ada
}
}
