css:
@-moz-document domain("wikipedia.org") {
/*font color*/
* {
               background-color: transparent !important;
               color: #bcbcbc !important;
}
.firstHeading{
    font-size:2.1em !important
}
#section_0{
    font-size:1.85em !important
}
/*links font color*/

a {
              
    color: #427dd7 !important
}
/*background color*/
body, #mw-head, .vectorTabs, .search-overlay li.page-summary, .search-overlay .search-content.overlay-header, .language-overlay .overlay-content, #main-menu-input:checked ~ #mw-mf-page-left, .toggle-list__checkbox:checked ~ .toggle-list__list, .oo-ui-popupWidget-popup, .vectorTabs li, .vectorTabs, .vectorTabs a, #mw-head .vectorMenu h3, #mw-createaccount-join, #mw-page-base, .oo-ui-popupToolGroup-tools, .oo-ui-windowManager-modal.oo-ui-windowManager-floating > .oo-ui-dialog > .oo-ui-window-frame, #editpage-copywarn > div:nth-child(1), .mw-prefs-buttons, .oo-ui-pendingElement-pending, .overlay-content, .overlay-footer-container {
               background: #1e1e1e !important;
}
.header-container.header-chrome, .header > div, #mw-mf-main-menu-button, body > div.overlay.language-overlay.view-border-box.visible > div.overlay-header-container.header-container.position-fixed > div > ul, body > div.mw-overlays-container > div > div.overlay-header-container.header-container.position-fixed > div, .drawer.references-drawer, .rt-tooltip, .frb-nag, .frb-fixed, #mw-mf-diffview #mw-mf-userinfo {
             background: #101010 !important  
}

/*logo*/
div.branding-box {
               height: 51.2px !important;
               width: 200px !important;
               padding-left: 0px !important;
               padding-top: 0px !important;
               background: url(https://i.imgur.com/ou7OU23.png) !important;
               background-color: transparent !important;
}

.mw-wiki-logo{
    background: url(https://i.imgur.com/EF2NY1q.png)
}
.mw-ui-background-icon-search{
    cursor:pointer !important
}
/*title*/

.page-heading {
            margin-bottom: 0px;
            font-size: 1.4em;
            text-align: center;
}

.heading-holder {
               padding: 30px 0px 0px;
}

#firstHeading, [id='bodyContent'] p, .vector-body p{
 padding-bottom:20px   
}
.content p{
    margin:0
}
tr, td, li.ext-related-articles-card, .referencetooltip > li, .mwe-popups, .mwe-popups-extract, .animations .mw-notification.visible, .animations .drawer.visible, .animations .mw-notification.mw-notification-visible, .animations .drawer.mw-notification-visible, .thumbinner, .toc, #simpleSearch, #searchform, .mwe-popups .mwe-popups-extract[dir='ltr']:after {
               background: #171717 !important
}

/*some other details*/
#mw-mf-page-center > header > form > div.branding-box > a, .clear, #mw-content-text > div.mw-search-form-wrapper, .language-overlay .search, .language-overlay .panel, .ext-related-articles-card-list h3:after, #mw-mf-page-center > header > form > div:nth-child(5), #mw-mf-page-center > header > form > div:nth-child(4), .mw-ui-icon.mw-ui-icon-element.mw-ui-icon-wikimedia-menu-base20.mw-ui-icon-flush-left.toggle-list__toggle:nth-child(3), #mw-mf-page-center > header > div > div > a {
               display: none !important
}

    
.minerva-header .branding-box
{
    min-width:200px !important
    }
.oo-ui-toolbar-bar{
    background:#1e1e1e !important
}

.overlay.search-overlay .search-box, .overlay.search-overlay .search-content, .overlay.search-overlay .spinner-container, .overlay.search-overlay .results{
    margin-left:0px !important
}



.overlay.search-overlay .overlay-title {
               width: 700px;
               padding-left: 15vw;
               ;
}
.ext-related-articles-card-list .ext-related-articles-card > a {
               position: unset;
               width: 983px;
}
div.ext-related-articles-card-detail {
               background-color: transparent !important
}
.vector-menu-tabs li, .vector-menu-tabs, .vector-menu-tabs a, #mw-head .vector-menu-dropdown h3.vector-menu-tabs, .vector-menu-tabs a, #mw-head .vector-menu-dropdown h3{
    background-image: none !important ;
}
nav ul {
               padding-bottom: 0
}

.content table.infobox th, .content table.infobox td, .content table.infobox, .site-link-list li, .search-box .search, .client-js .toc-mobile, li.gallerybox div.thumb, #mp-topbanner, #mp-left, #mp-right, #mp-lower, #mp-other-lower, #mp-tfa-h2, #mp-itn-h2, #mp-itn > div:nth-child(1), #mp-dyk-h2, #mp-otd-h2, #mp-tfp-h2, #mp-other, #mp-sister, #mp-lang, #mw-mf-page-left ul li, .mw-body, h1, h2, h3, h4, h5, h6, .infobox, .thumbinner, .thumbimage, #searchInput, .navbox, .catlinks, .toc, .toccolours, table.ambox,.rt-tooltip, #mw-mf-diffview #mw-mf-userinfo, .page-list li, .topic-title-list li, .site-link-list li, .mw-parser-output .mw-authority-control .mw-mf-linked-projects, .navbox-list, footer .last-modified-bar, .page-actions-menu, .minerva-footer, tr + tr > .navbox-abovebelow, tr + tr > .navbox-group, tr + tr > .navbox-image, tr + tr > .navbox-list, .mw-echo-ui-notificationBadgeButtonPopupWidget-popup > .oo-ui-popupWidget-popup > .oo-ui-popupWidget-head, .mw-echo-ui-notificationItemWidget, .overlay.search-overlay .search-content, .overlay-footer-container, #mw-content-text > div.mw-parser-output > table.mbox-small.plainlinks.sistersitebox, .mw-parser-output .portal, .overlay-header, .header-container, .oo-ui-buttonElement-framed > .oo-ui-buttonElement-button, .language-searcher .site-link-list a {
               border: none !important;
}

body > div.overlay.search-overlay.visible > div.overlay-content > div.results, .overlay.search-overlay .search-content.overlay-header {
               width: 100% !important;
               margin: 0px !important;
}

.overlay-header {
               width: 100%
}
#mw-mf-page-left, #mw-mf-page-left > div {
               width: 300px
}
.oo-ui-buttonElement-button, #mw-mf-page-center > footer > div.post-content.footer-content > div.minerva-footer-logo > img{
 filter:invert(1)   
}
.primary-navigation-enabled.animations .drawer .position-fixed, .primary-navigation-enabled.animations #mw-mf-page-center {
               transform: translate(300px, 0)
}
.oo-ui-popupWidget-popup{
        border: 0.5px solid #6d6d6d;
}
.language-overlay .overlay-content-body {
               height: 650px
}
body > div.overlay.language-overlay.view-border-box.visible > div.overlay-header-container.header-container.position-fixed > div {
               margin-left: 0px;
               margin-right: 0px;
               max-width: 100% !important
}
#mw-mf-page-center > header > form > div.branding-box {
               position: absolute;
               left: 531.5px;
}
#mw-mf-page-center > header > form > div.search-box, .search.mw-ui-background-icon-search.skin-minerva-search-trigger {
               width: 25px;
}
   .search.mw-ui-background-icon-search.skin-minerva-search-trigger{
        position:relative;
        left:28vw !important
    }
    
    .minerva-header .branding-box {
        position:relative;
        left:12vw;
        opacity: 0.88;
}
    
    
body > div.mw-overlays-container > div > div.overlay-header-container.header-container.header-chrome.position-fixed > div > div > form > input {
               position:relative;
               text-align: center;
}
body > div.mw-overlays-container > div > div.overlay-header-container.header-container.header-chrome.position-fixed > #searchInput {
               width: 155px
}
body > div.mw-overlays-container > div > div.overlay-header-container.header-container.header-chrome.position-fixed > div > ul {
               right: 495px;
               top: 14px;
               position: absolute;
}
.overlay.search-overlay .results, .overlay.search-overlay .overlay-content, body > div.mw-overlays-container > div > div.overlay-content > div.search-content.overlay-header, body > div.mw-overlays-container > div > div.overlay-content > div.results > div {
               width: 100%;
               max-width: 100%;
               margin-left: 0px;
}
body > div.mw-overlays-container > div > div.overlay-header-container.header-container.header-chrome.position-fixed > div > div.header-action > button {
               right: 19vw;
                top:1.5px
}
#mw-mf-main-menu-button {
               width: 130px;
}

.mw-body-content{
 top: 4vh;   
}
.firstHeading{
  position: relative;
    top:2vh;
    
}
#mw-mf-diffview .mw-diff-inline-added ins, #mw-mf-diffview .mw-diff-inline-changed ins {
    background-color: #75c877 !important;
}
ins, del{
    color:#333 !important
}
#mw-mf-main-menu-button, .mw-editsection .ve-edit-source, .mw-editsection .ve-edit-visual, .mw-ui-icon-element{
    color:transparent !important
}
#mw-mf-diffview .mw-diff-inline-deleted del, #mw-mf-diffview .mw-diff-inline-changed del {
    background-color: #e07076 !important;
}
.mw-mf-bytesremoved{
color:red !important;
}
.mw-mf-bytesadded {
    color: #14866d !important;
}
.mw-ui-icon-with-label-desktop{
    color:#838383 !important
}


#mw-mf-page-center > footer > div.post-content.footer-content > h2 > img, .oo-ui-icon-settings, .mw-ui-icon-settings:before, .oo-ui-widget.oo-ui-widget-enabled.mw-echo-ui-notificationBadgeButtonPopupWidget.mw-echo-ui-notificationBadgeButtonPopupWidget-alert{
    filter:invert(1)
}

/*** Scrollbar ***/

::-webkit-scrollbar {
    width: 8px!important;
    height: 8px!important;
}

::-webkit-scrollbar-button {
    width: 0px!important;
    height: 0px!important;
}
::-webkit-scrollbar-thumb {
    background: #909090!important;
    border-radius: 10px!important;
}
::-webkit-scrollbar-thumb:hover {
    background: #606060!important;
}
::-webkit-scrollbar-thumb:active {
    background: #606060!important;
}

::-webkit-scrollbar-corner {
    background: transparent!important;
    
    }

}
