css:
@-moz-document domain("roblox.com") {
.icon-default-common, .icon-play-game {
    background-image: url("https://roblox.com/images/Shared/common_dark_12032018.svg");
    transform: rotate(180deg);
    margin-left: 15px;
}

.icon-play-game:after {
    background-image: url("https://roblox.com/images/Shared/common_dark_12032018.svg");
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 36px;
    height: 36px;
    margin-left: 18px;
    display: inline-block;
    vertical-align: middle;
    transform: rotate(0deg);
    content: ' ';
}

.btn-common-play-game-lg, a.btn-common-play-game-lg {
    background-color: #ff1414;
    border-color: #ff1414;
}

.btn-common-play-game-lg:hover, a.btn-common-play-game-lg:hover {
    background-color: #ff4848;
    border-color: #ff4848;
}

.icon-dislike {
    background-position: -0px -84px;
}

.icon-dislike:hover {
    background-position: -28px -84px;
}

.icon-dislike.selected {
    background-position: -28px -84px;
}

.icon-like {
    background-position: -0px -112px;
}

.icon-like:hover {
    background-position: -28px -112px;
}

.icon-like.selected {
    background-position: -28px -112px;
}

.voting-panel .users-vote .vote-details .vote-container .vote-background.has-votes {
    background-color: #E27676;
}

.voting-panel .users-vote .vote-details .vote-container .vote-percentage {
    background-color: #02b757;
    transform: rotate(180deg);
    float: right;
    position: sticky;
}

.voting-panel .users-vote .vote-details .vote-numbers .count-left {
    float: right;
    color: #02b757;
}

.voting-panel .users-vote .vote-details .vote-numbers .count-right {
    float: left;
    color: #E27676;
}
}
