css:
@-moz-document domain(coub.com) {

body {
    font-family: helveticaneuecoub-normal, Helvetica, Arial, "Lucida Grande", sans-serif;
    font-weight: 400;
    color: #ffffff;
    min-height: 100%;
    overflow-x: hidden;
    overflow-y: scroll;
    background-color: #000000eb;
    -webkit-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-size: 16px;
    font-size: 1rem;
    line-height: 1.05;
}
element.style {
}
.header.-scrolled>.header__bg {
    background: #2727273d;
}
.page-menu::before {
    content: '';
    height: 100%;
    width: 10000px;
    left: -5000px;
    position: absolute;
    background-color: #000000eb;
}
element.style {
}

.header__bg {
    background: #000000eb;
    transition: all .2s ease-in-out;
    box-shadow: 0 1px 0 0 #000000eb;
}	


element.style {
}
.coub.coub--normal-card .description__title, .coub.coub--normal-card .description__title>a {
    font-size: 20px;
}
.coub__description .description__title, .coub__description .description__title>a {
    font-size: 16px;
    line-height: 1.3;
}
h5, h5>a, .h5, .h5>a {
    font-size: 18px;
    font-size: 1.125rem;
    line-height: 1;
}
a {
    color: #ffffff;
    text-decoration: none;
}
	
.main-menu__wrapper {
    height: 100%;
    width: 240px;
    background-color: #141414;
    box-shadow: 0 0 0 1px rgba(0,0,0,0.05);
    visibility: hidden;
    transition: transform 0.15s ease, visibility 0s linear 0.15s;
    transform: translateX(-100%);
}

.header__bg {
    background: #141414;
    transition: all .2s ease-in-out;
    box-shadow: 0 1px 0 0 #141414;
}

.description__controls>div i svg path {
    fill: #ffffff;
}

.header.-scrolled>.header__bg {
    background: #141414;
}

.-color--black {
    color: #ffffff;
}

.page-menu::before {
    content: '';
    height: 100%;
    width: 10000px;
    left: -5000px;
    position: absolute;
    background-color: #141414;
}

.page-menu {
    display: flex;
    border-bottom: 1px solid #141414;
    margin-bottom: 20px;
    justify-content: space-between;
    align-items: center;
    background-color: #ffffff;
    height: 60px;
    position: relative;
}

.page-menu .dropdown--select .dropdown__handler .sb.-toggle {
    font-size: 14px;
    background: none !important;
    box-shadow: none !important;
    color: white;
    font-family: helveticaneuecoub-normal, Helvetica, Arial, "Lucida Grande", sans-serif;
    font-weight: 400;
    height: 55px;
    white-space: nowrap;
}

.sb.-toggle:after {
    content: '';
    margin-top: -2px;
    top: 50%;
    right: 10px;
    border-left: 4px solid transparent;
    border-right: 4px solid transparent;
    border-top: 4px solid #ffffff;
    width: 0;
}

.page-menu__inner:not(.-mouse-over) .page-menu__item:not(.list__item).-active::before, .page-menu__item:not(.list__item).-hovered::before {
    content: '';
    position: absolute;
    bottom: -3px;
    left: 0;
    width: 100%;
    height: 3px;
    background-color: #ce0c83;
    border-radius: 2px;
}


.coub__tags-tag.tags-community, .story__tags-tag.tags-community {
    color: #000000;
    background-color: #ce0c83;
    border-radius: 15px;
}

.page-menu__item:not(.list__item) span {
    color: #ce0c83;
}

.main-menu__hr {
    height: 1px;
    background-color: #ce0c83;
    margin: 10px 20px;
}
.coub__tags-tag.tags-community, .story__tags-tag.tags-community {
    color: #000000;
    background-color: #ce0c83;
    border-radius: 15px;
}

.dropdown__content {
    text-align: left;
    position: relative;
    background-color: #141414;
    box-shadow: 0 1px 3px rgba(255, 255, 255, 0);
    border-radius: 8px;
    overflow: hidden;
    z-index: 0;
}

.dropdown__content {
    text-align: left;
    position: relative;
    background-color: #50414847;
    box-shadow: 0 1px 3px rgba(54, 65, 204, 0);
    border-radius: 8px;
    overflow: hidden;
    z-index: 0;
}

.coub.coub--normal-card .description__title {
    margin: -2px 120px 0 0;
    color: #ffffff;
    line-height: 1.19;
}

.nav__item .nav__item__inner {
    font-size: 16px;
    color: #ffffff;
    padding-left: 28px;
    white-space: nowrap;
}
}
