css:
@-moz-document url-prefix("https://www.qbittorrent.org/"), url-prefix("http://192.168.1.13:8080/") {
html, body,  #Filters {
    background: #111 !important;
    color: #ccc;
}

a {
 	color: rgb(30,160,210) !important;
}

#desktopNavbar li a {
    color: #ccc !important;
    border-bottom-color: #1c2538 !important;
}

#desktopNavbar li a:hover {
    background: #444 !important;
}

#desktopNavbar li ul {
    background: #333 !important;
    background-image: none !important;
    border:1px solid #111;
    border-top-color: #444;
}

#desktopNavbar li ul .divider {
    border-top-color: #1a2130 !important;
}

#pageWrapper {
    border-top-color: #111 !important;
}

.torrentTable, .dynamicTable {
    border-color: #111 !important;
}

.torrentTable th, .dynamicTable th {
    background: #2c2c2c !important;
    border-bottom:2px solid #1c1c1c;
    color: #fff !important;
}

.dynamicTable th {
    border-right-color: #1c2538 !important;
}

.torrentTable tr, .dynamicTable tr {
    background: #222 !important;
    color:#ccc;
}

.torrentTable tr:nth-child(even), .dynamicTable tr:nth-child(even) {
    background: #1c1c1c !important;
}

.torrentTable tr.selected, .torrentTable tr:hover, .dynamicTable tr.selected, .dynamicTable tr:hover {
    color: #fff !important;
}

.torrentTable tr.selected, .dynamicTable tr.selected {
    background: rgb(65, 75, 122) !important;
    color: #000;
}

.torrentTable tr:hover, .dynamicTable tr:hover {
    background: #444 !important;
}


.toolbarTabs {
    padding-bottom: 0 !important;
}

.toolbarTabs li, .toolbarTabs li a {
    background-image: none !important;
    color: #fff !important;
}

.toolbarTabs li {
    margin-left: 2px !important;
    margin-right: 2px !important;
    margin-bottom: 0 !important;
    background: #333 !important;
    border-radius: 0;
    box-shadow: 0 0 7px #111;
    background-position: left -70px
}

.toolbarTabs li.selected {
    background: #4c4c4c !important;
    
}

.toolbarTabs li a {
    margin-left: 0 !important;
    padding: 4px 9px !important;
}

#propertiesPanel_header {
    height: 27px !important;
}

#propertiesPanel_headerContent {
    padding-top: 3px !important;
}

#prop_general fieldset, #watched_folders_tab tr, #watched_folders_tab th, #watched_folders_tab td {
    border-color: #222 !important;
    color:#ccc;
}

#desktopFooter, #desktopHeader, #desktopNavbar, #mochaToolbar{
    background: #333 !important;
    color: #fff !important;
}

.panel {
    background: #222 !important;
    border-bottom-color: #1c2538 !important;
    color: #fff !important;
}

.panel-header, .panel-headerContent, .toolbarTabs {
    background-image: none !important;
    background: #222 !important;
}

.panel-header {
    border-bottom-color: #1c2538 !important;
}

.horizontalHandle, .columnHandle {
    background: #222 !important;
    border-color:  #222 !important;
}

.columnHandle:after {
    transform: rotate(90deg) !important;
}

.horizontalHandle {
    background-image: none !important;
}

.mochaCanvas {
    display: none !important;
}

.mochaTitlebar, .mochaTitlebar.replaced {
    background: #222 !important;
    border-radius: 7px 7px 0 0;
}

.mochaTitle {
    color: #fff !important;
}

.mochaToolbarWrapper {
    border-top-color: #222 !important;
}

.mochaToolbar {
    border-top-color: #222 !important;
    background: #222 !important;
    padding-top: 4px;
}

#mochaToolbar .divider{
        background-image:none;
        border-left:1px solid #666;
}

.mochaContentBorder {
    border-top-color: #222 !important;
    border-bottom-color: #222 !important;
    border-bottom-width: 23px !important;
    border-radius: 0 0 7px 7px;
}

.mochaContentWrapper {
    background: #222 !important;
}

.mochaContent fieldset {
    border-color: #1c2538 !important;
}

.mocha::after {
    content: "";
    display: block;
    position: absolute;
    top: 4px;
    left: 5px;
    bottom: 7px;
    right: 5px;
    border-radius: 7px;
    box-shadow: 0 0 10px #000;
    z-index: -1000;
}

.mocha#preferencesPage::after {
    bottom: 6px;

}

.mochaMinimizeButton.replaced, .mochaMaximizeButton.replaced, .mochaCloseButton.replaced{
            background-image:linear-gradient(rgba(0, 0, 0, .4), rgba(0, 0, 0, .4))!important;
            border-radius: 50%;
}

.contextMenu, .contextMenu li ul {
    background: #2b2b2b !important;
    border-color: #000 !important;
}

.contextMenu li a {
    color: #ccc !important;
}

.contextMenu li a:hover {
    background: #444 !important;
}

h2, h3 {
    color: #fff;
}

.filterTitle{
    padding-bottom:10px;
    padding-top:10px;
}

.filterList a {
    color: #ccc !important;
    padding-left:20px;
}

.filterList li.selectedFilter {
    background: #333 !important;
}

.filterList li:hover{
    background: #222 !important;
}

.filterList img{
    filter: brightness(0) invert(.3) sepia(1) hue-rotate(140deg) saturate(14) !important;
}

/*Makes the toolbar buttons, rightclick menu and menu bar white*/
.mochaToolButton, .contextMenu img, #desktopNavbar img, #addTrackersPlus{
    filter: grayscale(100%) invert(100%) !important;
}

.mochaToolButton:hover{
    filter: grayscale(100%) brightness(0) invert(100%) !important;
}

/*Adjusts images in the footer and filter menu and torrents section (e.g. the downloading, seeding and paused icons) to be slightly brighter*/
#filtersColumn img, #transferList_wrapper img, #desktopFooter img{
    filter: brightness(1.5);
}

/*Modify the black images to gray (e.g. the idle upload and download)*/
#transferList_wrapper img[src*="images/skin/stalledUP.svg"], #transferList_wrapper img[src*="images/skin/stalledDL.svg"]{
    filter: invert(50%);
}

/* % of download completed*/
.progressbar_dark {
    background: #3e88ad !important;
}

/* % of download uncompleted*/
.progressbar_light {
    background: #7da8bc !important;
}

.panel-collapse {
    background: url(http:../theme/go-down) center center no-repeat;
    background-size: 24px;
}

select {
    background:  rgb(50,50,65);
    border-color: #000;
    color: #eee !important;
}

input, textarea {
	background: #1d1d1d !important;
    color: #fff !important;
    border: 1px solid;
    border-color:   #444;
    white-space: nowrap;
}

input::placeholder {
    color: #aaa !important;
}

#downloadPage, #downloadPage_overlay, #downloadPage_contentWrapper, #downloadPage_iframe {
    height: 600px !important;    
}

#uploadPage, #uploadPage_overlay, #uploadPage_contentWrapper, #uploadPage_iframe {
    height: 420px !important;  
}

#error_div {
    color: #0f0;
}
}
