css:
@-moz-document url-prefix("https://codeforces.com/") {
body{
    background-color: rgb(29, 29, 29);
    color: green;
}

.ttypography{
    color: green !important;
}

.content{
    border-left: 4px green solid !important;
}

.menu-list-container{
    background-color: rgb(29, 29, 29);
}

.roundbox{
    border: 1px solid green !important;
    padding: 0px;
}

.roundbox-rt, .roundbox-lb, .roundbox-rb, .roundbox-lt, .rt, .lb, .lt, .ilt, .irt{
    display: none;
}

.menu-list-container > ul > li > a{
    font-weight: bold;
    color: green;
}

.alert-success{
    background-color: rgb(29, 29, 29) !important;
    text-shadow: none;
    color: green !important;
    border: 1px solid green !important;
}

.roundbox{
    background-color: rgb(29, 29, 29) !important;
}

.bordertable{
    color: green !important;
    font-weight: bold;
}

.pagination > ul > li > span > a:hover{
    color: green;
    font-weight: bold;
}

.pagination > ul > li > span.active{
    background-color: rgb(29, 29, 29) !important;
    border: 0px;
    color: green;
}

.pagination > ul > li > span.active > a{
    color: green;
    font-weight: bold;
}


.pagination .inactive{
    color: black;
}

.rtable > tbody > tr > td{
    background-color: rgb(29, 29, 29) !important;
}

.rtable > tbody > tr > td.dark{
    background-color: rgb(35, 35, 35) !important;
}

.bottom-links{
    background-color: rgb(29, 29, 29) !important;
    border-top: 1px solid green !important;
}

input{
    background-color: rgb(29, 29, 29) !important;
    border: 0px;
    color: green !important;
}
input[type="submit"]{
    font-weight: bold;
}
select{
    background-color: rgb(29, 29, 29) !important;
    border: 0px;
    color: green;
    border: 1px solid green;
    border-radius: 5px;
}

input.search{   
    background-color: rgb(29, 29, 29) !important;
    border: 0px;
    font-weight: bold;
    color: green;
}

.roundbox .titled{
    border-bottom: 1px solid green;
}

.roundbox .caption{
    color: green;
}

a{
    color: #49ff00 !important;
}

.current > a{
    color: green !important;
    font-weight: bold;
}

.backLava{
    display: none !important;
}

.second-level-menu-list > li > a:hover{
    color: green !important;
}

tr, th, td.dark, td.state{
    background-color: rgb(29, 29, 29) !important;
}

.datatable > div{
    background-color: rgb(29, 29, 29) !important;
}

.verdict-rejected{
    font-weight: bold;
	color: #ff0000d9 !important;
}

.problems .rejected-problem{
	color: #ff0000d9 !important;
}

h3{
    color: #49ff00 !important;
}

.groupName{
    color: #49ff00 !important;
}
.datatable td, .datatable th{
    border: 1px solid green;
}

.datatable{
    background-color: green !important;
}

.roundbox .rtable td, .roundbox .rtable th{
    border: 1px solid green; 
}

.menu-list li.current{
    border-bottom: 3px solid #49ff00;
}

.problems .accepted-problem td.id {
    border-left: 6px solid #49ff00 !important;
}

.problems .accepted-problem td.act {
    background-color: rgb(29, 29, 29) !important;
}

.countdown{
    color: #49ff00 !important;
}

div.ttypography .MathJax{
    color: #49ff00 !important;
}

.problem-statement .sample-tests pre{
    background-color: rgb(29, 29, 29) !important;
}

div.ttypography pre, div.ttypography code{
    color: green;
}

.sidebar-menu ul li:hover, .sidebar-menu ul li.active{
    background-color: rgb(35, 35, 35);
    border: 1px solid green;
}

.sidebar-menu ul li {
    border: 1px solid green;
}

.highlighted-row td, .highlighted-row th {
    background-color: rgb(29, 29, 29) !important;
}

td:hover{
    background-color: rgb(40, 40, 40) !important;
}

#program-source-text, div.ttypography pre, div.ttypography code {
    background-color: #ddd;
}
}
