css:
@-moz-document domain("www.apparata.nl") {
BODY, #ab_main_body, .ab_top_header, .ab_main_content, .ab_pushmenu-push, DIV.ab_blog_wrapper.list-item, .ab_tags_container { background: #111 !important; color: #aaa !important }
 
#ab_main_nav_container .ab_search_bar { background: none !important; border: none !important }

A, A:hover, .ab_detail_content p a, .ab_comment_section .ab_comment_reply a, .ab_nav_next a .ab_nav_label, .ab_nav_previous a .ab_nav_label { font-weight: normal !important; color: #1EAEDB; text-decoration: none }
A:hover, .ab_nav_next a .ab_nav_label:hover, .ab_nav_previous a .ab_nav_label:hover    { text-decoration: none !important; color: red !important }
.ab_login_wrap { background-color: rgba(255, 255, 255, 0.4) }
.ab_login_wrap a { color: #eee }
.ab_widget_service_voor_jou ul li a, .ab_widget_check_je_kenteken .widget-btn { background-color: #222; border: 1px solid #333}
.fa-chevron-right:before { content: '' }
.ab_search_bar input[type="button"] { background-color: #333 }
.ab_search_bar .wrapper { padding: 0 0 0 27px }
#ab_main_nav_container .ab_search_bar { height: auto }

.ab_lp_wrapper { border-bottom: 0 }

nav.ab_main_nav ul      { background-color: #111 }
nav.ab_main_nav ul li   { background-color: #111 !important; border: none !important; margin: 0; }
nav.ab_main_nav ul li a { margin: 0; color: #fff }
nav.ab_main_nav ul li a:hover { background-color: #222; border: none !important }

DIV.top-discussions a { color: #111 !important; border: 1px solid #333 }
DIV.top-discussions a span { color: #fff;  }

H1, H1 A, .ab_comments_body A, H2, FIELDSET#replyform, FIELDSET#replyform span, P.ab_subtitle { color: #aaa !important; text-shadow: 0px 0px #444; font-weight: normal !important }
LABEL, LEGEND { font-weight: 400 }

SPAN.page-numbers.current { color: #fff; padding-left: 7px; padding-right: 7px }
SPAN.page-numbers.dots { color: #fff; padding-left: 4px; padding-right: 4px }
.ab_subtitle { color: #aaa !important }
.ab_widget_actueel, .ab_tag_wrapper, .ab_widget_check_je_kenteken, .ab_widget_service_voor_jou, .ab_widget_de_vraag, .ab_widget_hete_discussies, .ab_widget_list_photo_intro, .ab_widget_news_list_links { background-color: #111; color: #aaa !important }

.ab_widget_actueel A, .ab_widget_check_je_kenteken A, .ab_widget_service_voor_jou A, .ab_widget_hete_discussies A, .ab_widget_list_photo_intro A, .ab_widget_news_list_links A { color: #ccc !important }

.ab_select  { background-color: #222; color: #ccc; font-size: 85%; border-color: #444; border-radius: 3px }
OPTION      { background-color: #222; color: #fff; font-size: 110% }

small { font-size: 100% }
input[type="submit"]{ color: #eee; background-color: #222 }
input[type="submit"]:hover { color: red; border-color: red;  }

DIV.ab_comments_body   { background-color: #181818 !important; color: #ccc !important; border: 0; border-radius: 0px 0px 5px 5px  }
DIV.ab_comments_header { border: 0; background-color: #222 !important; border-radius: 5px 5px 0px 0px; padding: 15px !important; line-height: 14px }
DIV[style='background-color:#FFFFCC !important'] { color: #000; filter: grayscale(100%) invert(91.75%)  }
.ab_comment_section .ab_comments_header .ab_comment_actions { margin-top: 10px; max-width: 65% }
.ab_comment_section .ab_comments_body { font-size: 14px; padding: 15px; padding-top: 10px }
.dwqa-questions-list .dwqa-question-item .dwqa-question-stats span { color: #fff !important; border: 0; }

.ab_widget_meest_besproken .one-article img:hover { filter: contrast(110%) brightness(110%) saturate(110%); box-shadow: 0 0 5px 0px #eee }

.ab_blog_img IMG, .ab_detail_single_image IMG, IMG.centered { border-radius: 5px }
.ab_blog_wrapper, DIV.lg_gallery DIV.item { background: none }
.ab_content_wrapper, .ab_detail_content.widget .four:nth-child(2), .ab_detail_content.widget { border: none }
.ab_detail_content p { color: #999 }

.ab_comment_section .ab_comments_header .ab_comments_details .ab_user, 
.ab_comment_section .ab_comments_header .ab_comments_details .ab_user a,
.ab_comment_section .ab_comments_header .ab_comments_details .ab_detail a,
.ab_comments_body A, H2, FIELDSET#replyform, FIELDSET#replyform span, P.ab_subtitle, .ab_blog_wrapper .ab_blog_info,
.ab_blog_wrapper .ab_blog_author a, .ab_blog_wrapper .ab_blog_comments a, .ab_blog_wrapper .ab_blog_date a,
.ab_lp_wrapper p, .ab_lp_wrapper .ab_lease_portal_info,
.ab_lp_wrapper .ab_lease_portal_author a, .ab_lp_wrapper .ab_lease_portal_comments a, .ab_lp_wrapper .ab_lease_portal_date a
{ color: #999 !important; font-size: 13px; text-decoration: none }

.lg_gallery .item a img { border:0; border-radius: 5px }
.lg_gallery .item a img:hover { box-shadow: 0px 0px 15px #ccc }

.rsThumbsHor { border-color: #000 !important; padding-bottom: 0px !important }
.rsDefault .rsThumb:not(.rsNavSelected) { background-color: #000 !important }
.rsDefault .rsThumb:not(.rsNavSelected) img { opacity: 0.5 }
.rsDefault .rsArrowIcn, .rsDefault .rsThumbsArrowIcn { filter: invert(100%) hue-rotate(180deg) }
.rsDefault .rsThumbsArrow { background-color: rgba(0,0,0, 0.3) !important }

.tablepress .even TD { background: #111 !important; color: #ccc }
.tablepress .even TH { background: #111 !important; color: #ccc }
.tablepress .odd  TD { background: #222 !important; color: #ccc }
.tablepress .odd  TH { background: #222 !important; color: #ccc }

footer, #ab_footer_banner_ad, DIV#ab_banner_ad  { display: none !important }
textarea    	 { background-color: #222 !important; color: #ccc; margin-top: 15px }
blockquote    	 { border-radius: 5px; background-color: #222 !important }

.ab_comment_section .ab_comment_reply { border-top: 0; padding-top: 0 }

.ab_blog_wrapper .ab_blog_info { padding-bottom: 10px }
.ab_advance_search { background: linear-gradient(0deg, #aaa, #ccc); border-radius: 5px; border-bottom: 0; padding-top: 0; padding-left: 8px }
}
