css:
@-moz-document domain("just-eat.fr") {
body, .decorator .page.serp .main, .restaurant, .order-summary>.wrapper, .footer, .decorator.checkout .page.confirmation .main .content .order-details, .decorator.home .page.home .hero .block {
    background-color: #262a2e !important;
    color: #888 !important;
}
.header, .header:after {
    background-color: #151515;
}
a.minicart-toggle {
    color: #FFF !important;
}
.partners {
    display: none;
}
textarea, .decorator .page.serp .main .wrapper .content .restaurants .restaurants-search-form .form-field label input {
    color: #666 !important;
    background: #151515 !important;
}
.decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(2) .tab.menu .products-wrapper .group .products .product>div:nth-child(3) .button {
    background-color: #151515;
}
.decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(2) .tab.menu .products-wrapper .group .products .product>div:nth-child(3) .button:not([disabled]):hover, .decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(2) .tab.menu .products-wrapper .group .products .product>div:nth-child(3) .button:not([disabled]):focus, .decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(2) .tab.menu .products-wrapper .group .products .product>div:nth-child(2) .button {
    background-color: #000 !important;
}
.decorator .page.serp .main .wrapper .content .navigation>ul>li>ul li a, .decorator .page.serp .main .wrapper .content .navigation>ul>li>ul li a.active, .decorator .page.serp .main .wrapper .content .navigation>ul>li>ul li a:hover, .order-summary .service-type .form-field.radio-field span strong, .decorator .page.serp .main .wrapper>.heading h2, h1, h2, h3, h4, h5, h6, a, .decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(2) .tab.menu .products-wrapper .group .products .product>div:nth-child(2) .description, .decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(2) .tab.reviews .form-field.checkbox-field label span {
    color: #888 !important;
}
.decorator .page.tr .main>.wrapper .restaurant-header>.delivery .service-type-info ul li {
    padding: 10px 20px;
    background: #444;
    color: #CCC;
}
.decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(3) form .button.checkout:not(.preorder)[disabled] {
    color: #CCC!important;
    background-color: #626f91!important;
    border-color: #626f91!important;
}
.decorator .page.tr .main>.wrapper .restaurant-content>div:nth-child(1) nav ul li a:hover, .form-field.select .selectboxit-container .selectboxit-options>.selectboxit-focus>.selectboxit-option-anchor, .decorator .page.serp .main .wrapper .content .navigation a.active, .decorator .page.serp .main .wrapper .content .navigation a:hover {
    background: #444;
}
.form-field.select .selectboxit-container .selectboxit {
    border-color: #151515;
}
.form-field.select .selectboxit-container .selectboxit.selectboxit-enabled:active, .form-field.select .selectboxit-container .selectboxit.selectboxit-enabled:focus, .form-field.select .selectboxit-container .selectboxit.selectboxit-enabled:hover {
    border-color: #666;
}
.form-field.select label:before, .decorator .page.serp .main .wrapper .content .navigation>ul>li>a {
    background-color: unset;
}
.form-field.select .selectboxit-container .selectboxit.selectboxit-enabled:active, .form-field.select .selectboxit-container .selectboxit.selectboxit-enabled:focus, .form-field.select .selectboxit-container .selectboxit.selectboxit-enabled:hover, .decorator .page.serp .main .wrapper .content .navigation>ul>li.service-type .form-field.checkbox-field label span, .decorator .page.serp .main .wrapper .content .navigation>ul>li.service-type .form-field.checkbox-field label input:checked+span, .search-form .form-field label input, .decorator.home .page.home .hero .block .search .search-form .form-field input:disabled~span, .decorator.home .page.home .hero .block .search .search-form .form-field input:focus~strong, .decorator.home .page.home .hero .block .search .search-form .form-field input:valid~strong {
    color: unset;
}
.form-field.select .selectboxit-container .selectboxit-options {
    background-color: #262A2E;
}

}
