css:
@-moz-document domain(buildyourownlisp.com) {
    body {
        background-color: #161922 !important;
        background-image: none !important;
        color:#BBBCCF !important;
        font-size: 16px;
    }
    pre {
        background-color:#1D1E21;
        color: white;
    }
    pre .comment {
        color: hsl( 95, 38%, 62%);
    }
    pre .keyword {
        color: hsl(286, 60%, 67%);
    }
    pre .keyword.operator {
        color: white;
    }
    pre .keyword.builtin {
        color: white;
    }
    pre .constant.numeric {
        color: hsl( 29, 54%, 61%);
    }
    pre .constant.string {
        color: hsl( 95, 38%, 62%);
    }
    pre .constant.character {
        color: magenta;
    }
    pre .meta {
        color: magenta;
    }
    pre .meta.preprocessor {
        color: hsl( 95, 38%, 62%);
    }
    pre .support.type {
        color: hsl(286, 60%, 67%);
    }
    pre .storage {
        color: magenta;
    }
    .alert-warning {
        background-color: #BDA801 !important;
        color: #272C34 !important;
    }
    code {
        background-color:#1D1E21;
        color: #fff;
        border-style:none;
        font-size:16px;
    }
    .alert {
        background-color: #262833 !important;
        color: #BBBCCF !important;
    }
    a {
        color:#2B79A2;
    }
    .list-group-item {
        background-color:#39415B;
    }
    .panel-heading {
        background-color:#39415B !important;
        color: #BBBCCF !important;
    }
}
