css:
@-moz-document domain("music.yandex.ru") 
{

.bar__content:hover .progress .progress__bg, .bar__content:hover .progress .progress__bar {
    height: 40px !important;
    line-height: 13px;
    -webkit-animation: progress__expand 0ms ease-out;
    -moz-animation: progress__expand 0ms ease-out;
    animation: progress__expand 0ms ease-out;
    font-size: 13px;
}
.progress__bar, .bar__content .progress_branding .progress__bg{
    height: 40px !important;    
}
.progress__right {
    margin-right: 10px;
    float: right;
    display: block;
    margin-top: 15px;
    font-size: 13px;
}
.progress__left {
    margin-right: 10px;
    float: left;
    display: block;
    margin-top: 15px;
    font-size: 13px;
}
.bar-below__content, .footer{display:none}

.bar {
    margin-bottom: 0px;
}
.d-input_transparent .d-input__field {
    border-color: #ffdb4d;
}

.page-root {
    margin: 0px 0 0 70px !important;
}
.notify {
    position: fixed;
    bottom: 105px;
    left: 15%;
    z-index: 13;
    width: 70%;
}
.deco-tooltip_notify {display:none}

}
