css:
@-moz-document domain("epoznan.pl") {
body {
    background: #111;
    color: #eee
}

#main_content {
    background-color: #222;
}

.new_tekst_tytul,
.news_lead {
    color: #7c86a2;
}

.comment_one_right_text {
    color: #cacaca;
    border-bottom: solid 1px #555;
}

.left_panel_1 {
    color: #7c86a2;
}

#top7_title,
.top7_news_right a {
    color: #7c86a2;
}

.top7_news_text a {
    color: #7c86a2;
}

#main_container_right,
#main_container_left,
#main_container_bottom {
    background: black;
}

div#tags_right span.news_tags a {
    color: #fff;
}

div#tags_right span.news_tags {
    background-color: #263446;
}

.header {
    background-color: #000;
}

td.menu_tree_main,
td.forum_login_main,
td.box_main {
    background-color: #000000;
}

.new_forum_big_window {
    color: #adadad;
    background: #1f1f1f;
}

#add_comment_form_header {
    background-color: #000000;
    color: #c5c5c5;
}

a {
    color: #5461a4
}

[id*="comment_one_a_click"] {
    background-color: #9f2929 !important
}

[id*="comment_one_text"] span {
    color: #a3a9bb !important
}

#comments_paginator_bottom a {
    color: #5166a2;
}

.new_forum_input {
    color: #bbb;
    background-color: #353535;
}

.new_forum_textarea,
.new_forum_textarea_right {
    color: #bbb;
    background-color: #353535;
}

#q_search {
    color: #bbb !important;
    background-color: #353535 !important;
}

.spam_komentarz {
    background-color: #ec8a00 !important
}

.comment_one_footer_links a.comment_one_footer_link_del {
    background-color: #ec8a00;
}

.comment_one_footer_links,
.comment_one_footer_links a {
    color: #ec8a00;
}

.comment_one_footer_score .number_of_not_ok {
    color: #ff0000;
}

.comment_one_footer_score .number_of_ok {
    color: #0f0;
}

.comments__buried {
    color: #fff;
    padding: 2px 4px;
    background-color: #ab1b1b;
}

.post__title {
    color: #a6a3ce;
}

.commentsForm__textarea {
    background-color: #000000;
    color: #eee
}

commentsForm__input {
    background-color: #000000;
    color: #eee;
}
}
