/* ==UserStyle==
@name           CoderPuppy's Spotify
@namespace      github.com/CoderPuppy/userstyles
@version        1.0.0
@description    Makes Spotify more compact
@author         CoderPuppy
==/UserStyle== */
@-moz-document domain("open.spotify.com") {
	/* better font */
	body,
	button,
	input {
		font-size: 11px;
		line-height: normal;
		letter-spacing: normal;
		font-weight: normal;
	}

	/* better font, single line tracks */
	.tracklist-name {
		font-size: 12px;
		line-height: normal;
		letter-spacing: normal;
		display: inline;
	}

	/* single line tracks */
	.track-name-wrapper .second-line {
		display: inline;
		margin-left: 1em;
	}

	/* TODO */
	/* 	.tracklist-row {
		outline: none;
	} */
	/* short tracks */
	.tracklist-row,
	.tracklist-col {
		height: 2.4em !important;
	}

	/* vertically center play/pause icon in track */
	/* 	.tracklist-top-align .icon-pause,
	.tracklist-top-align .icon-play {
		transform: translateY(2px);
	} */
	/* TODO */
	/* 	.tracklist-col.more {
		overflow-y: hidden;
	} */
	/* vertically center track menu icon */
	.tracklist-col.more .tracklist-top-align {
		margin-top: 0;
	}

	/* TODO */
	/* 	.tracklist-col.more .spoticon-ellipsis-24:before {
		margin-top: -4px;
		font-size: 18px;
	} */
	/* smaller cover art in tracklist (on artist page) */
	.tracklist-col.tracklist-col-cover-art-thumb .cover-art {
		width: 25px !important;
	}

	/* eliminate space around the content coming from padding-top on .content */
	.content {
		padding-top: 0;
	}

	/* eliminate space around the content coming from padding-{left,right} on .contentSpacing */
	.contentSpacing > .content {
		margin-left: -32px;
		margin-right: -16px;
	}

	/* eliminate space around the left column (in albums and playlists) */
	.content > .container-fluid > div > .col-lg-3 {
		padding-left: 0;
	}

	/* eliminate space around the right column (in albums and playlists) */
	.content > .container-fluid > div > .col-lg-9 {
		padding-left: 0;
		padding-right: 0;
	}

	/* make the left column smaller (in albums and playlists) */
	@media(min-width: 1200px) {
		.content > .container-fluid > div > .col-lg-3 {
			width: 15%;
		}
		.content > .container-fluid > div > .col-lg-9 {
			width: 85%;
		}
	}

	/* TODO */
	/* 	@media(max-width: 1199px) {
		.entity-info {
			padding-bottom: 0;
		}
	} */
	/* reduce space inside the navbar, smaller navbar */
	nav.navBar {
		padding: 1em 0 0 1em;
		width: 200px;
	}

	/* smaller navbar */
	.SearchInputBox {
		left: 200px !important;
	}

	/* smaller navbar */
	.Root__nav-bar {
		width: 200px;
		overflow-x: hidden;
	}

	/* horizontally center spotify logo in navbar */
	.navBar-header {
		justify-content: center;
	}

	/* smaller now playing bar */
	.now-playing-bar {
		height: 75px;
	}

	/* smaller now playing bar */
	.Root__nav-bar {
		height: calc(100vh - 75px);
	}

	/* hide ads container */
	.AdsContainer {
		display: none;
	}

	/* eliminate negative margin */
	.content.playlist > .container-fluid > .row.PlaylistContainer {
		margin-left: 0;
		margin-right: 0;
	}

	/* reduced padding in navbar */
	.navBar-link {
		padding-left: 15px;
		padding-right: 0;
	}

	/* reduced padding in navbar */
	.sessionInfo {
		margin-left: 0;
		margin-right: 0;
	}

	/* remove scrollbar */
	html {
		overflow-y: hidden;
	}

	/* remove download app button in sidebar */
	.NavBar__download-item {
		display: none;
	}

	/* remove playlist header from sidebar */
	.Rootlist__playlists-header {
		display: none;
	}

	/* compress sidebar */
	.Rootlist {
		margin-top: 0;
	}
	
	/* smaller navbar: smaller cover art */
	/* [data-testid=CoverSlotExpanded__container] */
	.NavBarFooter .d6e5892a336f6ae43bf066f2245c81b1-scss {
		margin-left: -1em;
		height: 200px;
	}
	
	/* smaller navbar: smaller cover art */
	.NavBarFooter .cover-art {
		width: 200px !important;
		height: 200px !important;
	}
}
