@-moz-document domain("www.achgut.com"), domain("newsletter.achgut.com") {
/* ==UserStyle==
@name         ACHGUT.COM Dark Theme (2019)
@namespace    de.sidneys.userstyles
@version      1.5.0
@description  A modern dark theme for ACHGUT.COM (DIE ACHSE DES GUTEN).
@author       sidneys <sidneys.github.io@outlook.com> (https://github.com/sidneys)
@homepageURL  https://userstyles.org/users/301302
@supportURL   https://forum.userstyles.org/messages/add/sidneys
@license      MIT
@preprocessor default
==/UserStyle== */
}

@-moz-document domain("www.achgut.com"), domain("newsletter.achgut.com") {
/* ====================================
   VARIABLES
   ==================================== */

/* GLOBAL
   ==================================== */

:root
{
  --color-container-1: rgb(26, 33, 48);
  --color-container-2: rgb(0, 20, 32);
  --color-container-3: rgb(37, 47, 68);
  --color-container-4: rgb(30, 30, 30);
  --color-text-1: rgb(230, 236, 247);
  --color-text-2: rgb(255, 255, 255);
  --color-text-accent-1: rgb(238,249,255);
  --color-text-accent-2: rgb(75, 190, 255);
}
}

@-moz-document domain("www.achgut.com"), domain("newsletter.achgut.com") {
/* ====================================
   ELEMENTS
   ==================================== */

/* GLOBAL
   ==================================== */

html,
html *
{
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-smooth: auto;
}

body
{
    color: var(--color-text-1);
    background-color: var(--color-container-1);
}

/* PAGINATION
   ==================================== */

.pagination a.active
{
    color: var(--color-text-2);
}

/* TEASER
   ==================================== */

.teaser_text a:hover,
.teaser_blog_text a:hover,
.section_leserpost .holder_pagination a span:hover
{
    opacity: 1;
    color: var(--color-text-accent-2);
}

.teaser_text_meta a
{
    color: var(--color-text-1);
}

.teaser,
.teaser_blog,
.themen2
{
    background: var(--color-container-4);
    border-color: var(--color-container-2);
}

.teaser_blog_text h3 a,
.teaser_text h2 a
{
    color: var(--color-text-2);
}

.teaser_text_meta
{
    margin-bottom: 10px;
}

.teaser_text h2 a
{
    font-size: 30px;
}

.teaser_text_meta a
{
    line-height: 16px;
    vertical-align: bottom;
}

.headline_section::after
{
	border-bottom-color: var(--color-container-1);
}

/* IMAGES
   ==================================== */

.social_blog_text img,
.teaser_text_meta img
{
    filter: contrast(0) sepia(100%) saturate(10000%) brightness(100%) hue-rotate(188deg);
}

.social_blog_text img:hover,
.teaser_text_meta img:hover
{
    filter: contrast(0) sepia(100%) saturate(10000%) brightness(200%) hue-rotate(188deg);
}

.social_blog_text img,
.teaser_text_meta img,
.teaser_text_meta a.comments:hover
{
    opacity: 1;
}


/* FORMS
   ==================================== */

.container_input_default,
.container_input_default textarea,
.formular input[type="text"],
.formular input[type="password"],
section.form div.form-inner input.text.e-mail
{
    background: var(--color-container-4);
}

.radio-custom-block,
.checkbox-custom-block
{
    background: var(--color-container-4) !important;

}

.container_input_default .kategorien_autoren_filter_label .checkbox-custom-label
{
    color: var(--color-text-2) !important;
}

.checkbox-custom + .checkbox-custom-label::before,
.radio-custom + .radio-custom-label::before
{
    background: var(--color-container-4);
}

.suche .checkbox-custom:checked + .checkbox-custom-label
{
    color: var(--color-text-accent-1) !important;
}

.suche .checkbox-custom:checked + .checkbox-custom-label::before,
.suche .radio-custom:checked + .radio-custom-label::before
{
	border-color: var(--color-text-accent-1) !important;
	background-color: var(--color-text-accent-1) !important;
}

section.form div.hint
{
    color: var(--color-text-2);
}
}

@-moz-document domain("www.achgut.com"), domain("newsletter.achgut.com") {
/* ====================================
   PAGES
   ==================================== */

/* NEWSLETTER
   ==================================== */

header.page-header
{
    border-bottom-color: var(--color-container-2);
}

section.introduction
{
    background: var(--color-container-4);
    border-bottom-color: var(--color-container-2);
}

/* BEITRAG
   ==================================== */

.beitrag
{
    background: var(--color-container-4);
    border-color: var(--color-container-2);
}

.leserbrief {
    background: var(--color-container-2);
    border-top-color: var(--color-container-2);
    border-bottom-color: var(--color-container-2);
}

.section_beitrag .teaser_text_meta
{
    margin-bottom: 0;
}

.teaser_blog_text h2
{
    color: var(--color-text-2);
}

}
