css:
@-moz-document domain("www.speedtest.pl") {
.meter-area .meter #start-stage {
    width: 698px;
    height: 375px;
    border: 1px solid #cccccc;
    position: absolute;
    top: 0px;
    left: 0px;
    display: none;
    background-color: #3d3a3a;
}
.topwrapper {
    background: #3d3a3a;
}
.ranktop {
    margin: 13px 0px 13px 0px;
    padding: 0px 0px 0px 45px;
    height: 38px;
    font-size: 27px;
    font-weight: bold;
    text-align: center;
    color: #f4f4f4;
}
.meter-area .meter-news .meter-news-box {
    width: 220px;
    height: 170px;
    float: left;
    text-align: center;
    font-size: 16px;
    opacity: 0.9;
    cursor: pointer;
    color: #f4f4f4;
}
body {
    margin: 0px;
    padding: 0px;
    font: 13px Arial;
    color: #f4f4f4;
    background-color: #1974cb;
}
.meter-area .meter-news h2 {
    margin: 0px 0px 16px 0px;
    padding: 0px;
    height: 16px;
    font-size: 16px;
    font-weight: bold;
    text-align: center;
    color: #f4f4f4;
    font-family: Arial;
}
.black {
    color: #f4f4f4;
}
.grey {
    color: #f4f4f4;
}
.meter-area .meter #loading-stage {
    width: 698px;
    height: 375px;
    border: 1px solid #cccccc;
    position: absolute;
    top: 0px;
    left: 0px;
    background-color: #333;
}
.meter-area .meter #testing-stage {
    width: 698px;
    height: 598px;
    border: 1px solid #cccccc;
    position: absolute;
    top: 0px;
    left: 0px;
    display: none;
    background-color: #3d3a3a;
}
.meter-area .meter .meter-result {
    width: 429px;
    height: 60px;
    position: absolute;
    top: 178px;
    left: 0px;
    text-align: center;
    font-size: 60px;
    line-height: 60px;
    color: #f4f4f4;
}
.meter-area .meter .meter-status .meter-testing {
    width: 120px;
    height: 12px;
    position: absolute;
    top: 0px;
    left: 40px;
    font-size: 12px;
    line-height: 12px;
    color: #f4f4f4;
}
.meter-area .meter .box-title {
    text-align: center;
    color: #f4f4f4;
    font-size: 16px;
    margin-bottom: 4px;
}
.meter-area .meter .ip-isp span {
    color: #f4f4f4;
}
.meter-area .meter .box-value span {
    font-size: 27px;
    color: #f4f4f4;
}
.wynik-box {
    font-family: 'Exo 2', sans-serif;
    width: 100%;
    border: 1px solid #00aaff;
    height: 275px;
    position: relative;
    background: #3d3a3a;
}
.wynik-background {
    background: #3d3a3a;
}
.wynik-box .box-title {
    text-align: center;
    color: #f4f4f4;
    font-size: 16px;
    margin-bottom: 4px;
}
.wynik-box .box-value-ping span {
    text-align: center;
    font-size: 21px;
    color: #f4f4f4;
}
.wynik-box .box-value span {
    font-size: 32px;
    color: #f4f4f4;
}
.wynik {
    width: 1195px;
    margin: 0px auto;
    color: #cccccc;
}
.result .box {
    background: #373737;
    border-radius: 7px;
}
.result {
    width: 1199px;
    /* margin-left: 11px; */
    color: #f4f4f4;
}
.wynik-box .ranking a {
    text-decoration: none;
    color: #f4f4f4;
}
}
