css:
@-moz-document domain("scnsrc.me") {
#header                                                             { margin: 0 auto }
#center, #footer, .comm_meta_wrap, .comm_meta_wrap2, .comm_meta,
.more_auto                                                          { background: #1c1c1c; color: #ddd }
#commentform input, #commentform textarea, #commentform #submit,
blockquote                                                          { background-color: #282828; color: #ddd; border-color: #333 }
a:hover, .content h3, .sidebar, .sidebar a                          { color: #ddd }
.menu                                                               { height: 36px }
.post                                                               { border-bottom: 0; margin-bottom: 50px }
.post img                                                           { border: 0 }
.sidebar a:hover, .sidebar h3                                       { color: #f60 }

.more.trigger, .wp-pagenavi a, .wp-pagenavi span                    { 
    color: #ddd !important; text-decoration: none; background: #333; margin: 8px; padding: 2px 8px;
    border-color: #830; border-radius: 3px; box-shadow: 0 0 7px 0 #f60; 
}
.wp-pagenavi                                                        { margin: 30px 0 0 0 }
.wp-pagenavi span.current                                           { margin: 8px; padding: 2px 8px }

.more.trigger:hover, .wp-pagenavi a:hover                           { background-color: #f60 }
}
