@-moz-document url-prefix(https://sjp.pl/)
{

  		body
        {
          background-color: black;
          color: white;
        }

        input[name="f_st"], input[name="f_en"], input[name="f_fl"],
        input[name="f_msfl"], select[name="f_mn"], select[name="f_vl"]
        {
          background-color: #d8d8d8;
          border: 1px solid #383838;
        }

        a:visited
        {
          color: #a884e6;
        }

        .aucmp
        {
          color: black;
        }

        span#t68806.tri0
        {
          color: #4286f4;
        }

        a:link, .lc
        {
          color: #4286f4;
        }

        input[value="sprawdź"]
        {
          color: white;
          border: 1px solid white;
            cursor: pointer;
        }

        .ifnvn
        {
          text-shadow:
              1px 1px black,
              -1px 1px black,
              1px -1px black,
              -1px -1px black;
        }

        a[rel="nofollow"]
        {
          font-weight: bold;
        }

        .ltab th
        {
          background-color: #413b42;
        }

        #optbox
        {
          color: black;
          border: 1px solid white;
          background-color: #bcbcbc !important;
        }

        input[value="wyszukaj"]
        {
          border: 1px solid #383838;
          background-color: #8e8e8e;
          cursor: pointer;
          color: white;
        }

        input[value="wyszukaj"]:hover
        {
          background-color: white;
          color: black;
          border: 1px solid #383838;
        }

        input[value="ok"]
        {
          color: white;
          border: 1px solid white;
        }

        textarea[name="post"], input[name="ncheck"], input[id="inp1"], div[class="aucmp"]
        {
          background-color: #bcbcbc !important;
        }

        #optctl
        {
          font-size: 15px;
          letter-spacing: 2px;
          font-weight: bold;
        }

        .y, .y a, .s
        {
          color: gray;
        }

        input#inp1
        {
          background-color: white;
          color: black;
          border: 1px solid white;
        }

        button
        {
          color: white;
          border: 1px solid white;
        }

        ul.m a
        {
        color: lightblue;
        text-decoration: underline;
        }
  
}
