css:
@-moz-document url-prefix("https://www.reddit.com/") {

    /* Main elements */

    html {
        font-family: "fira sans", sans-serif !important;
    }
    a, .search-expando-button  {
        color: #76319F !important;
    }
    a:visited {
        color: #8a689f !important;
    }
    body, a.search-comments.may-blank{
        font-family: "fira sans", sans-serif !important;
        color: #3F3B3B !important;
        background: #EDE6D5 !important;
    }
    input, textarea {
        background-color: white !important;
        border: 2px solid #D4CBB6 !important;
        border-radius: 8px !important;
        padding: 5px 10px !important;
    }
    blockquote {
        font-style: italic !important;
    }

    /* Code blocks */

    .md pre {
        background-color: transparent !important;
        border: none !important;
    }
    code, .md pre code {
        color: #333 !important;
        background: #efeadc !important;
        border: 1px solid #ccc !important;
        border-radius: 4px !important;

        /* Prevent inline <code> blocks from touching each other between lines */
        font-size: 95% !important;
    }
    .md pre code {
        /* Add padding only to <pre><code> blocks, not to all <code> blocks */
        padding: 0.5em !important;
    }

    /* Header */

    div#sr-header-area {
        background: white !important;
        border-bottom: 1px solid #D4CBB6 !important;
    }
    div#sr-header-area a.choice {
        background: none !important;
        color: black !important;
    }
    #sr-more-link {
        background-color: white !important;
    }

    #header, div.side {
        background: #F2EFE4 !important;
        border-bottom: 1px solid #D4CBB6 !important;
        padding: 0px 8px 8px 8px !important;
        text-align: center !important;
        font-size: .9em !important;
        font-weight: 400 !important;
    }
    #header {
        text-align: left !important;
        padding: 0px !important;
    }
    .tabmenu li a {
        border: 1px solid #D4CBB6 !important;
    }
    .tabmenu li.selected {
        background: #EDE6D5 !important;
        border: 1px solid #D4CBB6 !important;
    }
    .tabmenu li.selected a {
        border: 1px solid #D4CBB6 !important;
        border-bottom: 1px solid #EDE6D5 !important;
    }
    .tabmenu a {
        color: #3F3B3B !important;

    }
    #header a.choice {
        background: #F5F5ED !important;
    }
    #header .selected a.choice {
        background-color: #EDE6D5 !important;
    }

    .pagename {
        font-family: "fira sans", sans-serif !important;
        font-size: 4em !important;
        font-variant: none !important;
        font-weight: 600 !important;
        text-transform: capitalize !important;
    }

    #header-bottom-right, #header-bottom-right *, #header-bottom-right a.pref-lang.choice {
        background-color: #333 !important;
        color: white !important;
    }

    /* Sidebar */

    div.side {
        border-left: 1px solid #D4CBB6 !important;
        text-align: left !important;
    }
    .redditname a {
        color: black !important;
        font-family: "fira sans", sans-serif !important;
        font-size: 2rem !important;
        font-variant: none !important;
        font-weight: 600 !important;
        text-transform: capitalize !important;
    }

    #search input[type="submit"] {
        border: none !important;
        padding-right: 6px !important;
    }

    .sidecontentbox .content, .login-form-side {
        border: 1px solid #D4CBB6 !important;
        border-radius: 5px !important;
    }
    .gadget .reddit-entry a {
        color: #3F3B3B !important;
    }

    .titlebox .bottom {
        border-top: 1px solid #D4CBB6;
        color: #D4CBB6;
    }

    .fancy-toggle-button a {
        color: white !important;
    }

    /* Story list */

    p.title a, .search-title {
        color: #3F3B3B !important;
    }
    .titlebox form.toggle, .sidebox .spacer {
        background: #F2EFE4 !important;
    }
    span.domain a {
        color: #3F3B3B !important;
    }
    div.morelink  {
        text-align: center !important;
        border-radius: 20px !important;
        border: none !important;
        display: inline-block !important;
        padding: 5px 18px 2px 18px !important;
        background: #817F7F !important;
        color: white !important;
        height: auto !important;
        text-decoration: none !important;
    }
    div.morelink a{
        background: #817F7F !important;
        color: white !important;
        font-weight: normal !important;
        letter-spacing: 0px !important;
    }
    div.nub {
        display: none !important;
    }

    /* Story pages */

    .link .usertext-body .md, .linkinfo {
        background: #F2EFE4 !important;
        border: 1px solid #D4CBB6 !important;
        color: #3F3B3B !important;
    }
    .md {
        color: #3F3B3B !important;
    }
    a.author {
        color: #3F3B3B !important;
    }
    .thing .tagline .author.submitter {
        color: #76319f !important;
    }
    .c-btn-primary {
        border-color: #76319F  !important;
        background-color: #76319F !important;
    }
    .flat-list.buttons a {
        color: gray !important;
    }

    /* Search results */

    .search-expando.collapsed::before {
        background: rgba(245,245,237,0) !important;
        background: linear-gradient(to bottom,rgba(245,245,237,0) 0%,#f5f5ed 100%) !important;
    }

    /* Footer */

    .footer-parent {
        border-top: 4px solid #333 !important;
        padding-top: 15px !important;
        text-align: center !important;
        background: #434342 !important;
        color: #888 !important;
    }
    .footer-parent p {
        background: #434342 !important;
        color: #888 !important;
    }
    .footer {
        border: none !important;
    }
    .footer a, .bottommenu a {
        color: #ccc !important;
        text-decoration: none !important;
    }
    .debuginfo {
        background: #434342 !important;
    }

    #hsts_pixel {
        /* This "pixel.png" causes a white bar to appear between
.footer-parent and .debuginfo.  Since it's free-floating in the body,
not inside a DIV, its background-color can't be controlled, and it
causes a bar to appear which exposes the underlying BODY color.
Disabling padding, margin, border, and height all seem to have no
effect.  The only solution seems to be to hide it completely. */
        display: none !important;
    }

    /* Misc */

    .nextprev a {
        text-align: center !important;
        border-radius: 20px !important;
        border: none !important;
        display: inline-block !important;
        padding: 5px 18px 5px 18px !important;
        background: #817F7F !important;
        color: white !important;

        text-decoration: none !important;
    }

}
