css:
@-moz-document url-prefix("http://androdome.com/") {
#topbar {
    border: #000 1px solid;
    background-image: url(https://i.imgur.com/IODeiwf.png);
    height: 72;
    text-align: left;
    vertical-align: bottom;
}
#content {
    position: relative;
    display: block;
    z-index: 1;
    
}
body {
    margin-bottom: 0px;
    margin-top: 0px;
    font-family: "Comic Sans MS", "Comic Sans", "Courier New";
    background-color: #FFF;
}
#loginbar {
    border-top: #000000 0px solid;
    border-right: #000000 0px solid;
    font-family: "Verdana";
    font-size: 12px;
    position: absolute;
    top: 1;
    font-weight: bold;
}
#notifbar {
    font-family: "Verdana";
    border: #000 1px solid;
    font-size: 15px;
    position: absolute;
    right: 20px;
    top: 5;
    background-color: #FFF;
    color: #000;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 2px;
    padding-bottom: 2px;
    text-align: center !important;
}
img.fullbackground {
    margin: 0 auto;
    position: absolute;
    z-index: -1;
    top: 0;
    left: 0;
    margin: 0;
    padding: 0;
    width: 100%;
    /* alternative: right:0; */
    opacity: 0;
}
#absolute {
    top: 1px;
    width: 40px;
}
#topbar center {
    opacity: 0;
    width: 0px;
}
#navbar {
    border-bottom: #000 1px solid;
    border-right: #000 1px solid;
    border-left: #000 1px solid;
    text-align: center;
    font-family: 'Comic Sans MS', Verdana, sans-serif;
    font-size: 12pt;
    font-weight: normal;
    line-height: 2em;
    text-decoration: none;
}
#notifbar .wlink {
    color: #FDC752;
    font-size: 13px;
    font-weight: bold;
}
}
