css:
@-moz-document domain("www.metronieuws.nl") {
* {
    box-shadow: none !important;
/*     border-radius: 0 !important; */
    --opacity: .5;
    --scale: 1.7;
    --text-color: white;
    --text-shadow-line: 0px 0px 3px black;
    --text-shadow: var(--text-shadow-line), var(--text-shadow-line), var(--text-shadow-line), var(--text-shadow-line), var(--text-shadow-line);
}

body {
/*     background: url('file:///C:/Users/swchuong/Pictures/firefox/Wallpaper/17308_digiSE104_Joanna_Krupa%5BWallpaper%5D.jpg') fixed no-repeat right 25% / cover; */
    background:  url('http://i.imgur.com/Cl9gKcU.jpg') fixed no-repeat right 25% / cover;
}

/*Overlay*/ [class="css-euw1i2"] {
	background: transparent !important;
}

/*Header*/ 
    .css-1sk0npi {
        position: absolute;
        background-color: rgba(150, 150, 150, var(--opacity));
    }
    
    /*second header*/ .css-71ujnh {
        background-color: rgba(255, 255, 255, var(--opacity));
    }
    
    
    .css-1gk2oin {
/*         max-width:  85%; */
    }
    
    /*Button*/ li.css-1l1nddq:not(:hover) > a {
        opacity: var(--opacity);
    }
    
    /*Button*/ li.css-1l1nddq:nth-child(1) > a {
        opacity: 1 !important;
    }
/*Header*/

/*Content*/
    .css-1qlaiqu {
        background: transparent !important;
    }
    
    .css-1y0cnw3 > picture > img:not(:hover), div.css-1sojozp > a > div > div > picture > img:not(:hover), .css-1pcdq95 > img:nth-child(1) {
        opacity: var(--opacity);
    }
    
    div.css-1sojozp > a > div:hover img, div.css-1sojozp > div:hover img {
        opacity: 1 !important;
    }

    div.css-1sojozp > div {
        background: rgba(255, 255, 255, var(--opacity));
    }
    
    div.css-1sojozp:not(:nth-child(1)) > a > div {
        background: linear-gradient(to top, rgba(255, 255, 255, var(--opacity)) 48.5%, transparent 48%);
    }
/*Content*/
}

@-moz-document url("https://www.metronieuws.nl/"), regexp("https://www.metronieuws.nl/(video|werkgeld|relatieseks|Columnisten|nieuws/dossier|food|techgadgets|showbizz|tfravel|sport|horoscoop|commercie/winacties|lezerscolumn|nachtboek-van-een-eerstejaars|nieuws|lifestyle)") {
/*Front-pages*/ 
	.region-center {
		transform: scale(var(--scale), var(--scale));
		transform-origin: center 0;
		margin-top: 10px;
	}
}

@-moz-document url-prefix("https://www.metronieuws.nl/nieuws/"), url-prefix("https://www.metronieuws.nl/in-het-nieuws"), url-prefix("https://www.metronieuws.nl/video/"), url-prefix("https://www.metronieuws.nl/techgadgets/"), url-prefix("https://www.metronieuws.nl/lifestyle/") {
/*Nieuws Item*/ 
    div.css-1sojozp > a > div {
/*         background: linear-gradient(transparent 440px, rgba(255, 255, 255, var(--opacity)) 440px) !important; */
    }
 
    test {
        opacity: var(--opacity) !important;
    }
    
    .css-148rk96, .css-1msbwu4, .css-1jsoxct, .css-pk4k7f, .css-s6kj4u {
        border-radius: 0;
    }
    
    .region-sidebar_left, .region-top {
        display: none;
    }
    
    .css-1a1zzkh {
        transform: scale(var(--scale), var(--scale));
        transform-origin: center 0;
        margin-top: 20px;
        display: block;
        width: 648px;
    }
    
/*     .css-1vanv3l{
    display: unset;
    justify-content: unset;
    flex-wrap: unset;
    order: unset;
    width: 648px;
    } */
}
