css:
@namespace url(http://www.w3.org/1999/xhtml);



/* Display The Navigation, Video, Title Of The Video and Related Videos On Watch Page */



@-moz-document url-prefix(http://youtube.com/watch) , url-prefix(http://www.youtube.com/watch) {





/* Hidden ID's and Classes */



#logo, #lang-locale-picker-links-wrapper, #slogan, #watch-ratings-views, #watch-main-area, #watch-comments-stats, #watch-channel-vids-div, #watch-active-sharing, #watch-video-response, #footer, #copyright, #chrome-promo, #watch-promoted-vid, #watch-promoted-container, span.expand-header, .watch-arrow, #adblitz-promo-hdr, #adblitz-promo-ugc, .alignC {display: none !important;}

.QLIconImg {display: none !important;}



/* Navigation Bar Styling */



.search-form, #search-type-masthead, #masthead-search-term, .masthead-button-wrapper, .yt-button, .yt-button-primary, .nav-item, .master-sprite, .search-term-focus {background:#262626 !important;}

.nav-item {border:none !important;}

#bar {border:1px solid #4a4a4a !important;}

#bar a:hover {color:#494949 !important;}

#bar a:visited {color:#494949 !important;}

.confirmBox {background:#262626 !important;border:1px dashed #4a4a4a !important;color:green !important;}

.errorBox {background:#262626 !important;border:1px dashed red !important;color:#7a7a7a !important;}



/* Search Form Styling */



form.search-term {

background:#262626 !important;

font-weight:bold !important;

font-size:12pt !important;

padding-bottom:3px !important;

border:1px solid #fff !important;

}



.search-term {

color:#7a7a7a !important;

font-weight:bold !important;

padding-left:5px!important;

}



/* Seach and Upload Button Styling */



.yt-button, .yt-button-primary {

background:#191919 !important;

border:1px solid #4a4a4a !important;

text-decoration:none !important;

}

 

/* Video Quality Setting */



#watch-low-quality-link, #watch-high-quality-link {text-decoration:none !important;}

#watch-video-quality-setting a:hover {color:#5c5c5c !important;}



/* Video Page Formatting */



#baseDiv {padding:10px 10px 10px !important;width: 970px !important;}



/* Gray Background with Light Gray Text*/



body {background:#262626 !important;color:#bcb2cf !important;}



/* Body Link Colors */



body a:link {color:#7b7b7b !important;}

body a:visited {color:#7b7b7b !important;}



/* Center Video */



#movie_player {margin-top: 25px !important;}



/* Format Video Title */



div>h1 {

padding-top:3px !important;

padding-bottom:3px !important;

padding-left:10px !important;

margin-top:10px !important;

background:#191919 !important;

border:1px solid #4a4a4a !important;

text-align: left !important;

font-family: georgia !important;

font: bold 17pt Trebuchet MS !important;

color: #7a7a7a !important;

}



/* Related Videos Box Styling */



#watch-channel-vids-body, #watch-related-vids-body {border: 1px solid #5b5b5b !important;background:#353535 !important;}

#watch-channel-vids-body a, #watch-related-vids-body a {text-decoration:none !important;}

#watch-channel-vids-body a:hover, #watch-related-vids-body a:hover {color:#5c5c5c !important;}



}
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") { 

/* Main Changes */

#masthead .logo {display:none !important ;}
#baseDiv {padding-bottom: 0px !important;}
#masthead #lang-locale-picker-links-wrapper {display:none !important ;}
#masthead #top-margin-links-wrapper {margin: -10px 0 1px 0 !important; }
#masthead .nav-item {font-size: 13px !important; width: 80px !important; padding: 6px !important;}
div [class='bar'] {height: 28px !important;}
#search-form {padding: 2px 0 2px 250px !important; }

#masthead #upload-button { margin: -3px -1px 0 0 !important; }
div [id='watch-vid-title'] {margin: -15px 0 -3px 0 !important;}
#footer, #chrome-promo, #copyright {display: none !important;}
#masthead .search-term, #footer .search-term {height: 20px !important; padding: 0px 1px 2px 1px !important; margin-top: -3px !important;}
.search-wrapper .search-button-text, .search-wrapper .search-left-cap, .search-wrapper .search-right-cap {font-size: 11px !important; margin-top: -3px !important;}


/* Front Page Message areas */

#PRO-titlebar, #PRO-data, .feedmodule-promoted, .img-blue-bottom-cap, .feedmodule-upper-left-right-border-blue {display: none !important;} 


/* Front Page Sidebar*/

div [class='alignC xsmallText grayText padT3'] {display:none !important;}
.side-announcement-box, .img-yellow-message-top-cap.img-side-cap, .img-yellow-message-bottom-cap.img-side-cap {display:none !important;}
#homepage-side-content { margin-top: -14px !important;}
#homepage-whats-new-block, #watch-video-response {display:none !important;}

/* Video Page */

#movie_player {width: 960px !important; height: 580px !important; float: left !important;}
.watch-channel-subscribe {padding-top: 0px !important;}
#watch-other-vids {position: absolute !important; top: 649px !important; right: 5px !important;}
#watch-video-quality-setting {margin-top: -17px !important; }
#watch-channel-brand-cap, #watch-low-quality-link {display: none !important; }
#watch-channel-brand-div {padding-top: 14px !important;}
#watch-ratings-views {padding: 10px 5px 0 !important; }
#watch-high-quality-link {position: absolute !important; top: 665px !important; left: 530px !important;}


/* Subscribe Page */

#profile-side-content {padding-top: 697px !important }
.profileEmbedVideo {margin-left: -314px !important }


/* Firefox Scrollbar Width Fix */

html {overflow: -moz-scrollbars-vertical !important;}

/*Ads removal*/
#ad_creative_4, #ad_creative_2 {display:none !important;}

}
