css:
@-moz-document url(https://www.1limburg.nl) {
body {
    background: #333;
    color: #eee;
    font-family: Arial, sans-serif !important;
}
.header-region, .important-message, .content-clickable-image, .promo-feed-block .promo-content {
    background: #222;
    border-color: #1f1f1f;
    border-bottom: 1px #1f1f1f solid;
    border-top: 0;
}
#block-feature-user-user-logged-in-navigation>ul>li {
    border-color: #444;
}
#feature-user-profile-block-search-form .form-item, #feature-user-profile-block-search-form input[type=submit], #feature-user-profile-block-search-form.show-input .form-actions, #feature-user-profile-block-search-form.show-input input[type=submit] {
    background-color: #444;
    border-left: 0;
    border-bottom: 0;
}
#feature-user-profile-block-search-form.show-input input[type=text], .article-lead, .node-advertorial.view-mode-full .article-title, .node-article.view-mode-full .article-title, .promo-title h3, #files .location {
    color: #ddd;
}
a, a:visited, #block-feature-site-footer-combined-menu ul.menu a, #block-menu-menu-secties ul.menu a, .article-title a, .node-advertorial.view-mode-full .article-lead, .node-article.view-mode-full .article-lead,
div.item-list-pager a, div.item-list-pager span,
.article-title a:visited, .article-body p, .important-message, .promo-feed-block .promo-content a, div[id^=block-facetapi-] a, .page-search .container-content .search-tags-title {
    color: #ccc;
}
#live-tabs {
    background: #333;
}
#live-tabs>ul li.active a, .node-promoblock.view-mode-teaser .article-title, #block-menu-menu-doormat>ul.menu>li>a, .page-taxonomy .sidebar-second h5, div[id^=block-facetapi-] h2, .page-search .container-content .view-header,
#block-feature-article-recent-videos .slick-dots li.slick-active button:before, #block-feature-article-recent-videos .slick-dots li:hover button:before, #block-feature-site-promo-block .slick-dots li.slick-active button:before, #block-feature-site-promo-block .slick-dots li:hover button:before, .incontent-promos .slick-dots li.slick-active button:before, .incontent-promos .slick-dots li:hover button:before,
.article-body .h1, .article-body .h2, .article-body .h3, .article-body .h4, .article-body .h5, .article-body .h6, .article-body h1, .article-body h2, .article-body h3, .article-body h4, .article-body h5, .article-body h6,
#files .type{
    color: #e84e0f;
}
#live-tabs .refresh.active {
    background: #444;
}
.live-more {
    background: linear-gradient(to bottom,rgba(0, 0, 0, 0) 0,rgba(50, 50, 50,.7) 10%,#333 50%,#333 100%);
}
.node-advertorial.view-mode-full .article-img, .node-article.view-mode-full .article-img, .front .content-wrap:after {
    background: transparent;
}
.article-tags a, #block-feature-article-recent-videos .slick-dots li button:before, #block-feature-site-promo-block .slick-dots li button:before, .incontent-promos .slick-dots li button:before {
    color: #eee;
}
.promo-title h3 {
    background: #555 !important;
}
input[type=color], input[type=date], input[type=datetime-local], input[type=datetime], input[type=email], input[type=month], input[type=number], input[type=password], input[type=search], input[type=tel], input[type=text], input[type=time], input[type=url], input[type=week], select, textarea {
    background-color: #444;
    border-color: #222;
    color: #ddd;
}
.article-body p {
    text-align: justify;
}
#block-feature-article-previous-next-article {
    background: #2c2c2c;
}
.header-post, .header-pre {
    border-color: #222;
}
#feature-user-profile-block-search-form .form-item, #feature-user-profile-block-search-form .form-actions, #feature-user-profile-block-search-form input[type=submit] {
    background-color: #222;
}
.article-detail-wrapper:before, #live-tabs #latestarticles .content:before, #information-icons>div:before {
    background-color: #2a2a2a;
}
#block-feature-article-recent-videos, #block-feature-site-promo-block, .incontent-promos, #block-feature-article-most-read-articles>h4, .footer-pre, #live-tabs>ul {
    border-color: #2a2a2a;
}
.siteinfo img {
    background: #eee;
    padding: 10px;
    border-radius: 5px;
    border: 2px #e84e0f solid;
}
}
