css:
@-moz-document domain("trucksbook.eu") {
.page-head {
    padding: 20px;
    min-height: 85px;
    background-color: #555;
}
.wrapper {
    padding: 20px;
    background: #333;
    min-height: calc(100% - 195px);
    overflow: auto;
}
.panel {
    margin-bottom: 20px;
    background-color: #555;
    border: 1px solid #333;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,.05);
    box-shadow: 0 1px 1px rgba(0,0,0,.05);
}
.panel-heading {
    border-bottom: 1px solid #333;
    padding: 15px;
    text-transform: uppercase;
    color: #fcf8e3;
    font-size: 13px;
    letter-spacing: 1px;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #555;
}
a {
    color: #f5f5f5;
    text-decoration: none;
}
.profile-info .panel-footer {
    background-color: #555;
    border-top: none;
}
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #f2dede;
    background-color: #555;
}
.table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid #555;
}
.table-body {
    width: 100%;
    background-color: #555;
    border: 2px solid #555;
    border-radius: 4px;
    box-shadow: 0 1px 2px #555;
    margin-bottom: 10px;
}
.user-panel {
    background-color: #555;
    border: 1px solid transparent;
    border-radius: 4px;
    float: left;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
    margin-bottom: 15px;
}
.main {
    width: calc(100% - 240px);
    right: 0;
    position: absolute;
    background-color: #555;
    height: 100%;
    margin-left: 240px;
}
.main > footer {
    background-color: #555;
    width: 100%;
    padding: 15px;
    text-align: center;
}
table {
    background-color: #555;
}
#speed {
    width: 75px;
    position: inherit;
    top: 190px;
    left: 145px;
    text-align: right;
    font-family: Digital, Verdana, Geneva, sans-serif;
    font-size: 50px;
    color: #555;
}
#km {
    width: 95px;
    position: inherit;
    top: 253px;
    left: 100px;
    text-align: right;
    font-family: Digital, Verdana, Geneva, sans-serif;
    font-size: 25px;
    color: #555;
}
#trailerInfoWhat {
font-size: 16px;
text-align: center;
margin-top: -60px;
color: #555;
}
.header a {
    color: #fcf8e3;
}
#tempoSP {
    top: 110px;
    left: 188px;
    font-family: Digital, Verdana, Geneva, sans-serif;
    font-size: 15px;
    color: #555;
}
}
