css:
@-moz-document domain("developer.chrome.com") {
  body {
    --background: #303030;
    background-color: var(--background);
    color: #bbb;
  }
  body,
  table,
  code,
  kbd,
  pre,
  samp,
  .api {
    font-size: 1rem;
  }
  .gc-container,
  body > div {
    max-width: 100%;
    width: calc(100% - 4rem);
    margin: auto;
  }
  aside.caution,
  aside.note,
  aside.warning,
  div.caution,
  div.note,
  div.warning,
  p.caution,
  p.note,
  p.warning {
    background: #26455c;
    border: 2px solid #0005;
    color: #b3bcc2;
    max-width: none;
  }
  aside.caution code,
  aside.note code,
  aside.warning code,
  div.caution code,
  div.note code,
  div.warning code,
  p.caution code,
  p.note code,
  p.warning code {
    color: #ffde58;
  }
  aside.warning em,
  aside.warning strong,
  div.warning em,
  div.warning strong,
  p.warning em,
  p.warning strong {
    color: yellow;
  }
  pre a *:not(#foo),
  a,
  a:link,
  a:visited {
    color: #5ab5e8;
    font-weight: normal;
  }
  a:hover {
    text-decoration: underline;
  }
  #toc a {
    color: #ffa500 !important;
    font-weight: normal !important;
  }
  .api {
    color: #BBB;
  }
  .code, code, pre,
  .prettyprint {
    font-family: monospace;
    color: #888;
  }
  pre code {
    font-size: unset;
  }
  html,
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    font-family: sans-serif;
    color: #fff;
  }
  a h1,
  a h2,
  a h3,
  a h4,
  a h5,
  a h6 {
    color: unset;
  }
  tr {
    border: none;
  }
  code {
    color: #c49e81;
    font-family: monospace;
    font-size: 1.25rem;
  }
  table table code {
    word-wrap: break-word;
  }
  table table table table code {
    word-break: break-all;
  }
  .pln {
    color: #c4c29f;
  }
  .kwd {
    color: #d17506;
  }
  .typ {
    color: #aa9d6e;
  }
  a > .typ {
    color: unset;
  }
  .pun,
  .opn,
  .clo {
    color: #8f8b41;
  }
  .str {
    color: #77c283;
  }
  .com {
    color: #aa7e66;
  }
  .tag {
    color: #ff8762;
  }
  .atn {
    color: #f324a5;
  }
  .atv {
    color: #00c000;
  }
  .lit {
    color: #0f0;
  }
  .dec,
  .var {
    color: #cf67cf;
  }
  mark,
  pre b {
    background: #47453a;
  }
  pre {
    background: hsl(0, 0%, 15%);
    box-shadow: none;
    border: 2px solid #333;
    line-height: 1.5;
    white-space: pre-wrap;
  }
  table {
    margin: .75em 0;
  }
  figure img,
  td,
  th {
    border: none !important;
    padding: .25em .5em;
  }
  td p {
    font-size: .9rem;
    line-height: 1.4;
  }
  tr:nth-child(n+2) {
    border-top: 6px solid #0002;
  }
  .article-content tr:nth-child(2),
  .api-reference > * > table > * > :nth-child(2),
  .description > table > * > :nth-child(2) {
    border-top: none;
  }
  td {
    background: hsla(0, 0%, 100%, .04)
  }
  th {
    background: none !important;
    border: none;
    padding: 0 0 .5em 0;
  }
  th:not(:empty):after {
    content: ':';
  }
  main img {
    filter: invert(.85);
    transition: filter .25s .25s;
  }
  img:hover {
    filter: none;
  }
  #fatnav .expandee li.submenu .category,
  #fatnav .pillar .expandee .submenu,
  .article-content,
  .inline-toc .toc {
    border: none;
  }
  #fatnav .pillar.highlight .toplevel {
    color: white;
  }
  #fatnav .expandee {
    background: #555;
    padding-top: .25em;
    padding-bottom: 1em;
  }
  #fatnav .expandee a.highlight,
  #fatnav .expandee li.submenu.active {
    background: none;
    color: yellow;
  }
  #fatnav .expandee a:hover {
    background: none;
  }
  #fatnav .expandee a {
    display: inline-block;
    padding: 0;
    font-weight: normal;
    color: #44afff;
  }
  #fatnav .pillar.active .expandee {
    display: block;
    left: auto;
    right: 2rem;
    width: auto;
    box-shadow: 2px 4px 30px #0008;
  }
  #fatnav .pillar.active .expandee::after {
    content: none;
  }
  #fatnav .expandee li.submenu {
    color: #ccc;
    margin-top: 1em;
  }
  #fatnav .expandee li.submenu > ul {
    font-size: 1rem;
    padding: .5em 0 0;
  }
  #fatnav .expandee li {
    padding-left: 1em;
  }
  #fatnav .pillar.active {
    background: #fff1;
  }
  .inline-toc .toc .toc li,
  .pillar-content > .g-section:not(:last-of-type),
  .inline-site-toc li.toplevel > ol.toc > li {
    border-bottom: none;
  }
  .inline-site-toc li.toplevel > ol > li.show {
    background: none;
  }
  .inline-site-toc li li a,
  .inline-site-toc li li a:link,
  .inline-site-toc li li a:visited.inline-site-toc li li a,
  .inline-site-toc li li a:link,
  .inline-site-toc li li a:visited,
  .inline-toc #toc .toplevel > a {
    color: #28c2ff;
  }
  .inline-site-toc li li a:focus,
  .inline-site-toc li li a:hover,
  .inline-site-toc li li a:link:focus,
  .inline-site-toc li li a:link:hover,
  .inline-site-toc li li a:visited:focus,
  .inline-site-toc li li a:visited:hover {
    color: #28c2ff;
  }
  .inline-site-toc li li.selected > a {
    color: yellow;
  }
  .inline-site-toc li li.selected > a:hover {
    color: yellow;
  }
  [style*="background-color"] {
    color: #000;
  }
  #gc-footer {
    background: #0005;
    padding: 2em;
  }
  #logo {
    border-radius: 26px;
    padding: 8px 16px;
    background: white;
    box-shadow: 2px 2px 30px #111;
  }
}
@-moz-document regexp("https://developer\\.chrome\\.com/(apps|extensions)/(?!api_index).*") {
  .api-summary {
    border-spacing: 6px;
    border-collapse: separate;
  }
  .api .api-reference h2 {
    padding: 1rem 2rem;
    margin: 2rem -2rem 0 -2rem;
    background: #111;
    color: #fff;
    border-top: 4px solid #ed9000;
    border-image: linear-gradient(to right, #fa0, transparent) 100% 0;
  }
  .api .api-reference h3 {
    color: #ffcd00;
    font-size: 1.2rem;
    font-weight: normal;
    padding: .5rem 2rem .6rem 2rem;
    margin: 3rem -2rem 0 -2rem;
    background: hsl(0, 0%, 12%);
  }
  .api .api-reference .innerTable h3 {
    margin: -.5rem -.75rem .75rem;
    padding: .75rem;
  }
  .api .api-reference h2 + div > h3 {
    margin-top: 0;
  }
  .api-reference h3 + .summary {
    padding: .5rem 2rem .6rem 2rem;
    margin: 0 -2rem .5rem -2rem;
    background: hsl(0, 0%, 14%);
  }
  .api-reference .innerTable h3 + .summary {
    padding: .75rem;
    margin: -.75rem;
  }
  .api-reference td h3,
  .api-reference td h4,
  .api-reference td h3 + .summary,
  .api-reference td h4 + .summary,
  .api-reference td h4 + .summary td h3,
  .api-reference h4,
  .api-reference h4 + .summary {
    margin: 0 0 0 -1rem;
    padding: 0 0 0 1rem;
  }
  .description {
    margin-top: 1em;
  }
  td > *:first-child {
    margin-top: 0
  }
  .api .api-reference table table {
    margin: 0.25rem 0
  }
  .api .api-reference table table td,
  .api .api-reference table table th {
    padding: 0.5rem 0.75rem;
    line-height: 1.2
  }
  td > .optional {
    position: absolute;
    margin-top: 1.7em;
    display: block;
    color: #16d708;
    font-weight: normal;
    font-size: .65rem;
    line-height: 1.3;
  }
  tr[id^=property] > td:nth-child(2) {
    font-weight: bold;
    color: #d5d5d5;
    max-width: 22vw;
    overflow-wrap: break-word;
  }
  tr[id^=property] td {
    vertical-align: top
  }
  .api-reference td:nth-child(2) > code {
    word-wrap: break-word;
    word-break: break-all;
    min-width: 10em;
    display: block;
    margin-top: 0.25em;
  }
  .api .availability {
    color: #9ac897;
    background: #3e513e;
    padding: 2px 8px 4px;
    line-height: 1.2;
    border-radius: .5em;
    margin: 0 !important;
    font-size: .8rem;
    display: inline-block;
    position: relative;
    left: -8px;
  }
  .availability + p,
  p:first-child {
    margin-top: 0 !important;
  }
  td p:last-child {
    margin-bottom: 0 !important;
  }
  pre[data-filename]::after {
    background-color: #444;
    color: #fc0;
  }
  #intro .code {
    color: yellow;
    font-weight: bold;
  }
  h3 + dd {
    margin-top: .75em;
    margin-left: 0;
  }
  .availability + dd {
    margin-left: 0;
  }
  dl dt {
    font-style: italic;
    margin-top: .5em;
    margin-bottom: 0;
  }
  .api-reference td:last-child:not(:first-child) {
    width: 100%;
  }
  @media (max-width: 1699px) {
    .api-reference tr tr tr tr {
      display: flex;
      flex-direction: column;
      border-top-color: #0001;
    }
    .api-reference tr tr tr tr > :nth-child(2) {
      max-width: none;
    }
    .api-reference tr tr tr tr > :last-child {
      padding-left: 3em !important;
    }
  }
  @media (min-width: 1700px) {
    code {
      word-break: normal !important;
    }
    .api-reference td:last-child {
      width: 90%;
    }
  }
}
@-moz-document url-prefix("https://developer.chrome.com/extensions/api_index"),
url-prefix("https://developer.chrome.com/apps/api_index") {
  tr:nth-child(n+3) {
    border-top: 6px solid #0002;
  }
}
@-moz-document url-prefix("https://developer.chrome.com/webstore/overview") {
  img[width] {
    width: auto;
  }
}
@-moz-document url-prefix("https://developer.chrome.com/home"),
url-prefix("https://developer.chrome.com/webstore/images"),
url-prefix("https://developer.chrome.com/multidevice/webview/tipsandtricks"),
url-prefix("https://developer.chrome.com/multidevice/android/customtabs"),
url-prefix("https://developer.chrome.com/extensions/themes") {
  main img {
    filter: none;
  }
}

@-moz-document url-prefix("https://developer.chrome.com/multidevice/webview/workflow") {
  main img[src*="yeoman"] {
    filter: none;
  }
}
@-moz-document url-prefix("https://developer.chrome.com/webstore/branding") {
  p.standout {
    background: #333f27;
    border: 2px solid #b1ff0029;
    font-size: 1rem;
    color: #8ba930;
    max-width: 20em;
  }
}
@-moz-document url-prefix("https://developer.chrome.com/webstore/apps_vs_extensions") {
  p#lastword {
    background: none;
    border: none;
    margin: 0;
    padding: 0 0 1em 0;
    color: #65bf5c;
    font-style: normal;
    font-weight: bold;
  }
  p#lastword a {
    font-weight: bold;
  }
}
@-moz-document url-prefix("https://developer.chrome.com/multidevice/user-agent") {
  .pln {
    color: #9fce63;
  }
  .typ {
    color: #63cea6;
  }
}

@-moz-document url-prefix("https://developer.chrome.com/multidevice/faq") {
  dt {
    color: white;
    font-weight: bold;
    margin-top: 1.5em;
    margin-bottom: .5em;
  }
  .typ {
    color: #63cea6;
  }
}
@-moz-document url-prefix("https://developer.chrome.com/home") {
  #site-sections {
    background: none;
  }
  #site-sections h2::before {
    filter: invert(.4) sepia(1) saturate(3) hue-rotate(170deg);
  }
}
