css:
@-moz-document domain("emulatoronline.com") {
body, 
.container, .jumbotron, 
.dropdown-menu                                  { background-color: #222; color: #ddd !important; border: 0 }
.dropdown-menu > li > a                         { color: #aaa; background-color: #282828;  padding: 3px 15px }

input#term                                      { filter: invert(85%) }

.navbar-inverse .navbar-nav>.active>a,
.navbar-inverse .navbar-nav>.active>a:focus,
.navbar-inverse .navbar-nav>.active>a:hover,
.navbar-inverse .navbar-nav>.open>a,
.navbar-inverse .navbar-nav>.open>a:focus,
.navbar-inverse .navbar-nav>.open>a:hover       { background: #333 }

a:focus, a:hover                                { color: inherit }
mark, .mark                                     { background-color: #282828; border-radius: 5px; padding: 3px 5px }

.navbar-inverse                                 { border-color: #222 }
.page-header                                    { border-bottom: 1px solid #333 }

.container.inner-wrap                           { margin-top: 0 }

.breadcrumb                                     { background-color: #222; padding: 0 }
.breadcrumb > .active                           { color: #ddd }

.game                                           { background-color: #222; padding: 0 }
.game-item                                      { box-shadow: 0 0 5px 0 #333; background-color: #282828 }
.game-item:hover                                { box-shadow: 0 0 5px 0 #888; background-color: #282828 }

.list-group-item                                { background-color: #282828; border-color: #333 }

.pagination > li > a, 
.pagination > li > span                         { background-color: #282828; border-color: #333; padding: 3px 8px; margin: 2px; border-radius: 5px !important }


ins, 
iframe[name^='google_ads_frame'],
.col-advert-side
{ display: none !important; z-index: -99999 !important; left: -9999px !important; top: -9999px !important; width: 0px !important; height: 0px !important; 
  transform: translateY(-9999px) !important; opacity: 0 !important; visibility: hidden !important }
}
