css:
@-moz-document domain("al.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.article__label
{
	text-transform: capitalize!important;
}

.container,
.advtoprail__dropdown-user,
.page-type__home-beta .module__promo--inner,
.page-type__home-beta .section__nav--menu .active,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.page-type__article .main-wrapper--article {
    display: block!important;
}

.section--jobcategories .categories_wrap ul.categ_list {
    overflow-y: visible;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.river-item__headline-link
{
	font-size: 3.5rem!important;
}

#custom_obits .lists li.paper a,
#news-river p,

.river-item__summary
{
	font-size: 2.85rem!important;
}

.article__label
{
	font-size: 1.52rem!important;
}

selector
{
	line-height: 2.5rem!important;
}

selector
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

selector
{
    top: 10px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

selector
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

selector
{
	padding: 1.2rem!important;
}

selector
{
	padding: 13px 26px!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

.section--jobcategories .categories_wrap ul.categ_list li .category_cell {
    height: 16rem;
}

/* Widths 
_____________________________________________________________________________________________________*/

#content,
#news-river,
#main .wrapper,

.careers .section .section__body,
.page-type__home-beta .module__lead .article--lead .article__details,
.page-type__home-beta .module__lead .article--lead .article__details--headline,
.page-type__home-beta .module__lead .article--lead .article__label-wrap,
.page-type__home-beta .main-wrapper--home .section,
.page-type__home-beta .module__lead .article--lead,
.page-type__home-beta .module__lead .article--package,
.page-type__home-beta .module__type-only
{
	max-width: none!important;
}

.advtoprail__dropdown-user
{
	width: auto!important;
}

#content,
#main .wrapper,

.page-type__home-beta .module__lead .article--package,
.footer__disclaimer,
.page-type__article .main-wrapper--article
{
	width: 98%!important;
}

.advtoprail__weather-details button {
    width: 84%;
}

.advtoprail__usermenu-signout button,
.article__image-content {
    width: 50%;
}

}

@-moz-document domain("al.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
	.main-wrapper--two-column {
		grid-template-columns: 5fr 234rem 5fr;
	}

	.main-wrapper--two-column .main-wrapper-inner {
		grid-template-columns: 93rem;
	}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("al.com") {
/* Font Settings */

.advtoprail__weather-button--change-location,

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.advtoprail__weather-forecast-temperature,
.advtoprail__weather-button--change-location:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.advtoprail__dropdown-inner--weather,
.job_company_name,
.sharebar__bookmark--tooltip-text,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.article__label,
.advtoprail__weather-title,

[class="title"],
b,
h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.article .byline,
.advtoprail__weather-label,
.advtoprail__usermenu-nameplate,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("al.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.advtoprail,
.careers .section--featuredrecruitology,
.footer,
.queryly_search_header .queryly_aff_logo,
.toprail
{
	background: #060505!important;
	background-color: #060505!important;
}

.careers .section--jobvideos,
.page-type__home-beta .section__nav--menu
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#content,
#custom_obits .lists,
#main .wrapper,
#main,

.careers .section--featuredrecruitology .box,
.advtoprail__dropdown-user,
.advtoprail__mainmenu-menulist,
.advtoprail__weather-button--change-location,
.main-wrapper--two-column .main-wrapper-inner,
.main-wrapper--two-column
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.ds-button--secondary
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.ds-button--secondary::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.ds-button--secondary:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.ds-button--primary[aria-label="Sign out"]
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.ds-button--primary[aria-label="Sign out"]::before
{
	background: radial-gradient(circle, #000 0%, #d90008 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.ds-button--primary[aria-label="Sign out"]:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

selector
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
