/* Clean dark portainer UI by Laeonix, 
NOT A GLOBAL STYLE BUT PUBLISHED AS ONE DUE TO INABILITY TO SPECIFY URL!!!

----------------------------------------------- */

body{background: #333;font-family:Montserrat;color: #adadad!important;}
.row.header {
    height: 60px;
    background: #30426a;
    margin-bottom: 15px;
}

.datatable .footer {
    background-color: #30426a;
    color: #ffffff;
    overflow: auto;
}

.datatable .actionBar, .datatable .toolBar {
    color: #fff;
    padding: 10px;
}

.datatable .toolBar {
    background-color: #30426a;
    overflow: auto;
}

a {
    color: #6dbcff;
    text-decoration: none;
}

.widget {
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,.05);
    box-shadow: 0 1px 1px rgba(0,0,0,.05);
    background: #333;
    border: 1px solid transparent;
    border-radius: 2px;
    border-color: #e9e9e9;
}

.widget .widget-header {
    color: #adadad;
    background-color: #333;
    padding: 10px 15px;
    border-bottom: 1px solid #e9e9e9;
    line-height: 30px;
}

.text-muted {
    color: #adadad;
}

.widget .widget-body table thead {
    background: #333;
}

.form-control {
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    background-color: #333;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color .15s ease-in-out,-webkit-box-shadow .15s ease-in-out;
    transition: border-color .15s ease-in-out,-webkit-box-shadow .15s ease-in-out;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out,-webkit-box-shadow .15s ease-in-out;
}

.form-control, output {
    display: block;
    font-size: 14px;
    line-height: 1.42857143;
    color: #337ab7;
}

.table .table {
    background-color: rgba(0,0,0,.25);
}
