/* Main color : #161616 Dark main color : #323449 Accent color : #FF0C00 */
@-moz-document domain(wikipedia.org) {
    #mw-panel, #mw-head, #footer {
        display: none;
   }
    .mw-body {
        margin-left: 0;
   }
    #mw-head-base {
        margin-top: -10em;
   }
    body, .mw-body, .parsoid-body, .toc, .mw-warning, .toccolours, tbody, table.wikitable > tr > th, table.wikitable > * > tr > th, table.prettytable > tr > th, table.prettytable > * > tr > th, div.UitklapHead {
        background-color: #161616 !important;
        color: #fcfcfc !important;
   }
    .toc {
        border: 1px solid #323449;
   }
    .mw-body .firstHeading {
        color: #fcfcfc;
   }
    a, .toctext {
        color: #FF0C00 !important;
   }
    * {
        color: #fcfcfc !important;
        font-family: sans-serif !important;
   }
    div.thumbinner, html .thumbimage, .toccolours, .catlinks {
        background: none;
        border: 1px solid #323449 !important;
   }
    .app-badges {
        background: none;
        outline: none;
   }
    .lang-list-button {
        background: #161616;
        outline: none;
   }
    input, #typeahead-suggestions {
        color: #161616 !important;
   }
    .pure-button-primary-progressive {
        background: #FF0C00;
        border-color: #FF0C00;
