css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/* move the menu bar (if you wanna) */
#menubar_container {
position: fixed !important;
top: 0 !important;
right: -5px !important;

}
#fb_menubar {
width: 725px !important;
margin-left: 25px !important;
}
#fb_menu_home { 
display: none !important; 
}

/* move feeds to right */
#home_filter_list {
float: right !important;
position: relative !important;
left: 60px !important;
height: 210px !important;
width: 120px !important;
overflow-y: hidden !important;
}
.UIMutableFilterList_Tip , .UIMutableFilterList_Hide, .UIMutableFilterList_Toggle {
display: none !important;
}
#home_sidebar {
position: relative !important;
float: right !important;
margin-top: 220px !important;
left: -75px !important;
}

/* add scroll to highlights 
.UIHomeBox {
float: right !important;
}

.UIHotStream.UIStream {
  height:380px!important;
  overflow-y:auto!important;
}
*/

/*transparency*/
#presence
{
   opacity: 0.8 !important
}

#fb_menubar
{
   position: fixed !important;
   left: auto !important;
   right: auto !important;
   opacity: 0.8 !important
}

/*dropdown menus to be transparent and stay put when the page is scrolled*/

.fb_menu_dropdown
{
   position: fixed !important;
   opacity: 0.8 !important;
   top: 30px !important;
}

#presence #buddy_list_tab {

font-size:10px;
}


}
