css:
@-moz-document domain(tutorials.ubuntu.com){
body {
    margin: 0;
    font-family: 'Ubuntu', sans-serif;
    font-weight: 300;
    line-height: 1.5;
    min-height: 100vh;
    background: #575757;
    color: #fff;
}
.paper-drawer-panel-0 .left-drawer.paper-drawer-panel > #drawer.paper-drawer-panel {
    background: #f7f7f7;
    overflow-y: auto;
    z-index: 1;
}
google-codelab:not([theme="minimal"]) .google-codelab-step-0 .instructions.google-codelab-step, google-codelab-stepgoogle-codelab:not([theme="minimal"]) .instructions.google-codelab-step-0 .google-codelab-step {
    box-shadow: 1px 1px 2px #ccc;
    background: #575757;
    max-width: 800px;
    font-size: 14px;
    margin: 0 auto;
        margin-bottom: 0px;
    margin-bottom: 32px;
    color: #fff;
}
google-codelab:not([theme="minimal"]) .google-codelab-step-0 .instructions.google-codelab-step aside.warning, google-codelab-stepgoogle-codelab:not([theme="minimal"]) .instructions.google-codelab-step-0 .google-codelab-step aside.warning {
    color: #842;
    color: #fff;
}
tutorial-feedback > .tutorial-feedback {
    /*! box-shadow: 1px 1px 2px #ccc; */
    margin-left: -24px;
    margin-bottom: -24px;
    margin-right: -24px;
    margin-top: 2em;
    background: #575757;
}
google-codelab:not([theme="minimal"]) .google-codelab-step-0 .instructions.google-codelab-step aside.tip, google-codelab-stepgoogle-codelab:not([theme="minimal"]) .instructions.google-codelab-step-0 .google-codelab-step aside.tip, google-codelab:not([theme="minimal"]) .google-codelab-step-0 .instructions.google-codelab-step aside.special, google-codelab-stepgoogle-codelab:not([theme="minimal"]) .instructions.google-codelab-step-0 .google-codelab-step aside.special {

    border-color: #6a6;
    background: #ded;
    color: #464;
    border: 1px solid #eca918;
    background: rgba(236, 169, 24, 0.1);
    color: #fff;

}
.google-codelab-step-0 code {

    color: #e95420;
    background-color: rgba(0, 0, 0, 0.08);
    padding: 0.1em 0.3em;

}
.paper-radio-button-1 #radioLabel.paper-radio-button {

    line-height: normal;
    position: relative;
    display: inline-block;
    vertical-align: middle;
    margin-left: 10px;
    white-space: normal;
    color: #fff;

}
}
