css:
@-moz-document url-prefix("https://www.google.com") {
body {
	background: url(https://images.pexels.com/photos/62693/pexels-photo-62693.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260);
	background-size: 100% 100%;
	background-repeat: no-repeat;
	background-attachment: fixed;
}
.fbar, input[name=btnI], #sbfblt, .szppmdbYutt__middle-slot-promo, .fOwUFe, #hplogo {
	display: none;
}
#lga {
    border-radius: 5px;
    content: url(https://upload.wikimedia.org/wikipedia/commons/7/79/Adidas_Logo.png);
    height: 50% !important;
    width: 30% !important;
    margin-top: 10% !important;
}
}

@-moz-document url-prefix("https://www.google.com/search?") {
body {
	background: url(https://images.pexels.com/photos/62693/pexels-photo-62693.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260);
	background-size: 100% 100%;
	background-repeat: no-repeat;
	background-attachment: fixed;
}
.fbar, #lga, #body, input[name=btnI], #sbfblt, #tads, .action-menu, #appbar {
	display: none;
}
.jhp input[type="submit"], .sbdd_a input, .gbqfba, .SDkEP, .RNNXgb {
	color: black;
	box-shadow: 0px 3px 10px rgba(0,0,0,0);
}
#top_nav div, #appbar, #extabar, #topabar, #slim_appbar, #sbfrm_l, #resultStats,div.sfbg, div.sfbgg,#gb,#searchform,#gbw, .gb_mb.gb_R {
    background: rgba(0,0,0,0)
}
#rhs .kp-blk, #center_col .kp-blk {
    background: white;
}
#hdtb {
    border-bottom: 0px;
}
}
