css:
@-moz-document url-prefix("https://r3.minicrm.hu/"), url-prefix("https://r3.minicrm.io/"), url-prefix("https://r3.minicrm.ro/") {
#Page .mc-input .mc-input__select {
    background: #424242;
    border: none;
    border-bottom: 2px solid #777;
    color: #fff;
}
#Page .mc-textarea {
    background: #424242;
    border: none;
    border-bottom: 2px solid #777;
    color: #fff;   
}
#Page input {
    background-color: #424242;
    border: none;
    border-bottom: 2px solid #777;
    color: #fff;
}
#Page input[disabled], select[disabled], textarea[disabled] { background: #424242; }
#Page .mc-input {
    background: #424242;
}

.mc-separator { border: none; }


h1 { color: #fff; }
h2 { color: #fff; }
.mc-sidebar .mc-sidebar__title { color: #b2b2b2; }
.mc-sidebar .mc-separator { border: none; }

a { color: #039be5; }
a:visited { color: #039be5; }

.mc-datalist table tr td { color: #fff; }
.mc-datalist table tr td a { color: #fff; }
.mc-datalist table tr td a:visited { color: #fff; }

.btn-default { background: transparent; color: #fff; border-color: #999; }
.btn-success { background: #5cb85c; color: #fff; border-color: #5cb85c; }
.btn-primary { background: #428bca; color: #fff; border-color: #428bca; }

.mc-sidebar .mc-sidebar__filter .mc-sidebar-filter__selected::before { background-color: #303030 }
.well { background-color: transparent; }
#CustomFilter { background-color: #424242; }
.Arrow-Filter { border-bottom: 20px solid #424242; }
#Pagination div.PageNumber { background-color: transparent; border-color: transparent; }
#Pagination a { background-color: transparent; border-color: transparent; }
.table-striped>tbody>tr:nth-of-type(odd) { background-color: transparent; }
.table>tbody>tr>td { border-color: transparent; }

.modal { color: #333; }
.modal h1 { color: #333; }
.modal .btn { color: #333; }
.modal .btn-success { color: #fff; }
.TodoEdit .FormField .FieldLabel { color: #b2b2b2; }
.TodoEdit .btn { color: #333; }
.TodoEdit .btn-primary { color: #fff; }

#FilterControls #FilterLabel { color: #fff; }
#FilterControls .SavedFilter { color: #fff!important; }

.ProjectForm { background: #424242; border-color: #424242; color: #b2b2b2; }
.CollapsibleBox .Header--formbox .mc-formbox__title { color: #b2b2b2; }
body:not(.Telekom) .Form label { color: #b2b2b2; }
.AS3SalesStatus { color: #b2b2b2 !important; }
.AS3SalesStatus .StatusTree .statusLabel { color: #b2b2b2; }
.AS3SalesStatus .StatusTree .Expired .btn.statusLabel.active { color: #b2b2b2; }
.mc-iconlist .mc-iconlist__date-separator { color: inherit; border: none; }
.mc-iconlist .mc-iconlist__separator { border-color: #777; }
.ListSeparator { border-color: #777; }
.mc-sidebar .mc-sidebar__container--related-contacts .mc-sidebar__item .mc-sidebar__item-title .mc-sidebar__item-main-header .mc-sidebar__item-header-title { color: #fff; }
.TagEditor .tm-tag { background: #424242; border-color: #424242; color: #b2b2b2; }

.mc-pagination--desktop .mc-pager__pageNumber { background: transparent; border: none; }
.mc-pagination--desktop .mc-pager__pageList a { background: transparent; border: none; }
.mc-pagination--desktop .mc-pager__pageList .mc-pager__current { background: transparent; border: none; }
.mc-iconlist .mc-iconlist__item .mc-iconlist__container .mc-iconlist__more-row .mc-iconlist__more { background: transparent; border: none; }


.mc-page__dashboard--wrapped { background-color: #212121; }
.mc-maincontent--relatedcard  { background-color: #303030; }
.mc-account-card .CardHeader  { background-color: #303030; color: #fff; }
.Card .CardContent  { background-color: #303030; color: #fff; }
.Description { color: #b2b2b2; }
.DetailedForm .Form  { background: #424242; border-color: #424242; color: #b2b2b2; }
table.Form>tbody>tr>td:first-child { color: #b2b2b2; }

body {
	background: #303030;
    color: #fff;
}

.mc-page__content {
    background: inherit
}

.mc-sidebar {
	background: #212121;    
}

.mc-datalist table td:not(.mc-datalist__heading) {
	border: none;
}

#FixedHelp {
	background: #000;
    border: none;
    height: 140px;
}

#FixedHelp .HelpBar {
 	display: none;   
}

#FixedHelp .HelpDetails {
	background: inherit;
}

#FixedHelp .HelpDetails .InfoBox {
	border-color: #303030;
}

.mc-sidebar>span>div:not(.mc-sidebar__container) {
	display: none;
}
.mc-project-connections { display: none; }
div[title="Count of employees"] { display: none; }
div[title="Total revenue"] { display: none; }


}
