css:
/* CSBU stylesheet */
@-moz-document domain(bottomupcs.com)
{
	body {
		font-family: Open Sans, arial, sans-serif;
		/*    background-image: url(draft.png); */
		line-height: 1.428x571429;
		background: #333333;
		color: #bcbcbc;
	}

	@media screen and (min-width:1200px) {
		body {
			margin-left:20%;
			margin-right:20%;
		}
	}

	/* FIGURES AND LISTINGS */

	.example-title {
		background: #aaaaaa;
		color: #ffffff;
		font-weight:bold;
		padding: 10px;
		border-radius: 15px;
		font-size: 85%;
	}

	.table-title {
		background: #aaaaaa;
		color: #ffffff;
		font-weight:bold;
		padding: 10px;
		margin-bottom: 5px;
		border-radius: 15px;
		font-size: 85%;
	}

	.table th {
		padding: 5px;
	}

	.table td {
		border: 1px dashed #bbbbbb;
		padding: 5px;
	}

	.figure-title {
		background: #aaaaaa;
		color: #ffffff;
		font-weight:bold;
		padding: 10px;
		margin-bottom: 5px;
		border-radius: 15px;
		font-size: 85%;
	}


	.figure-contents {
		border-bottom: 1px dashed;
		padding-bottom: 5px;
	}


	.programlisting {
		border-left: 1px solid;
		border-right: 1px solid;
		border-bottom: 1px solid;
		margin: 0px 20px 0px 20px;
		padding: 20px;
		overflow-x: auto;
	}

	.figure {
		margin-left: auto;
		margin-right: auto;
	}
	.figure img {
		display: block;
		margin-left: auto;
		margin-right: auto;
	}
	.figure p {
		padding: 2px;
		text-align: right;
		background: #eee;
	}
	.figure .mediaobject p {
		background: none
	}

	.caption {
		font-size: 75%;
		color: #333;
		width: 55%;
		margin-left:45%;
	}

	/* TABLE OF CONTENTS */
	div.toc {
		border: 1px solid #7acc29;
		/* background: #B7E689; */
		background: #a2CC7a;
		color: #555555;
		padding: 5px;
	}

	div.list-of-figures, div.list-of-tables, div.list-of-examples {
		margin-top: 10px;
		border: 1px solid #cca300;
		background: #E6E68A;
		/* background: #cccc7a; */
		color: #555555;
		padding: 5px;
	}

	.toc-title {
		font-weight: bold;
	}

	/* TITLE */
	h1.title {
		/* color: #71A5D9; */
		color: #8CB3D9;
		padding: 5px;
		border-bottom: 5px solid #8CB3D9;
		background: #555555;
		text-align:center;
	}

	h2.title {
		border-bottom: 1px solid #aaaaaa;
	}

	/* FIGURE */
	/* hack just to get the title */
	.figure p b {
		background: #ddd;
		width: 400px;
	}


	/* HEADER */
	.navheader {
		background: #555555;
		color: #d9d9d9;
		border: 1px solid #898989;
		padding: 5px;
		font-size: 93%;
	}

	.navheader hr {
		padding: 0px;
		margin: 0px;
		border: 0px;
	}

	/* FOOTER */
	.navfooter {
		background: #555555;
		color: #d9d9d9;
		border: 1px solid #898989;
		padding: 5px;
		font-size: 93%;
		margin-top: 10px;
	}

	.navfooter hr {
		padding: 0px;
		margin: 0px;
		border: 0px;
	}

	.releaseinfo {
		background: #555555;
		color: #d9d9d9;
		border: 1px solid #898989;
		padding: 10px;
		font-size: 110%;
		margin-top: 10px;
		text-align:center;
	}


	.legalnotice {
		font-size: 90%;
		background: #555555;
		color: #d9d9d9;
		padding: 5px;
		text-align: center;
	}

	.copyright {
		font-size: 80%;
	}

	.abstract-title {
		font-weight: bold;
	}

	a.email, a.dsq-brlink, section.section a, .navheader a, .navfooter a {
		color: #71A5D9;
	}
}
