css:
@-moz-document domain("insidehook.com") {
.ad,
.ad-leader {
    display: none !important;
}
.body-nation .app-container {
    background-color: #211e1e;
}
.body-nation h1,
.body-nation h2,
.body-nation h3,
.body-nation h4,
.body-nation h5,
.body-nation h6 {
    color: #eee;
}
.body-nation {
    color: #eee;
}
.body-nation a,
.body-nation .zergheader {
    color: #30678a !important;
}
.body-nation .top-bar {
    background-color: #2f3746;
}
.body-nation .top-bar-section li:not(.has-form) a:not(.button) {
    background-color: #040607;
}
.body-nation [data-magellan-arrival].labels {
    background: none;
}
.body-nation .top-bar-section ul li, .body-nation .top-bar.expanded .title-area {
    background: none;
}

}
