css:
@-moz-document domain("youtube.com") {
.comment-thread-renderer {margin: 0 10px 0px 0!important;}
.comment-thread-renderer .comment-action-buttons-toolbar:nth-of-type(1) {margin-bottom: 15px!important;}
.comment-replies-renderer-header {position: relative!important; bottom: 19px!important;}
.spf-link[href*="jeyoung624"], .spf-link[href*="jeyoung624"] + *, .spf-link[href*="jeyoung624"] + * + *, .spf-link[href*="jeyoung624"] * + * + *, .spf-link[href*="jeyoung624"] + * + * + * + * {display: none!important;}
.comment-replies-renderer {height: 1px!important; transition: .1s!important; overflow: hidden!important;}
.comment-thread-renderer:hover .comment-replies-renderer {height: auto!important; overflow: visible!important; transition: .1s!important;}
}
