css:
@-moz-document domain("facebook.com") {
/* lean more about my custom css at https://peshigome.jp/dev/stylish */

/* Background image (change https://cdn.peshigome.jp/stylish/facebook/your_name.jpg to change background image)*/
.UIPage_LoggedOut {
    background-image:url('https://cdn.peshigome.jp/stylish/facebook/your_name.jpg');
    background-repeat: no-repeat;
    background-position: center top;
	background-attachment: fixed;
	background-size:cover;
    overflow: hidden;
}

/* top bar remove */
.UIPage_LoggedOut ._53jh {
    background: transparent; 
    border: none;
}

/* remove all other stuff than login form */
.UIPage_LoggedOut div:nth-child(3) {
    display: none;
}

/* set up login form location */
.loggedout_menubar_container {
    position: fixed;
    height: 82px;
    min-width: 0;
    top: 41%;
    left: 27%;
    margin-top: -17px;
    margin-left: -210px;
}

/* set up login form part coherence */
.loggedout_menubar_container .lfloat,
.loggedout_menubar_container .rfloat { 
    float: none;
}

/* set up login form coherence */
.loggedout_menubar_container .rfloat form table { 
    display: block; 
    margin: 0 auto;
}

/* set up login form apparence */
.loggedout_menubar {   
    background: black;
    background: rgba(0,0,0,.5);
    padding:0 42px 14px 10px;
    border-radius: 4px;
    width: auto;
}


/* align facebook logo */
.loggedout_menubar .fb_logo {
    margin-left: 14px;
}

/*  give top space to input */
.menu_login_container {
    margin-top:1.2em;
}

/* set up input apparence */
.menu_login_container td .inputtext{
    background: black;
	background:rgba(0,0,0,0.4);
	border-radius:3px;
    color: white;
}
}
