css:
@-moz-document domain("mail.google.com"), domain("mail.google.com.br"), domain("hangouts.google.com") {

/***** HEADER *****/
.nH.oy8Mbf.qp {
	background: rgb(81, 134, 236) !important;
	box-shadow: 0 0 4px rgba(0, 0, 0, .14), 0 4px 8px rgba(0, 0, 0, .28);
}
.gb_Zd.gb_ve.gb_7d {
	height: 56px;
	padding: 0;
}

/* rainbow logo */
img.gb_va {
    display: none;
}
.gb_2c {
    min-width: auto !important;
}

/*** header icons ***/
.gb_sc > svg,
svg.gb_ff,
svg.gb_gf {
	color: white;
	opacity: 1;
}
/*** "gmail" logo ***/
 a.gb_Fe.gb_oc > img.gb_0a {
	display: none;
}
a.gb_Fe.gb_oc:before {
	color: white;
	content:"Gmail";
	display:inline-block;
	font-size: 1.5em;
}
/*** search bar ***/
.gb_bf.gb_Lf {
	background: rgba(255, 255, 255, .15);
	height: 33px;
	border-radius: 2px;
	border: 1px transparent;
	margin-top: 11px;
	margin-left: 9px !important;
}
.gb_bf.gb_Lf:hover {
	background: rgba(255, 255, 255, .3) !important;
}
.gb_Of.gb_If {
	height: 32px !important;
	padding: 0 !important;
}
input[type="text"].gb_xf {
	padding: 0;
	height: 34px;
}
.gb_bf button {
	padding: 4px !important;
}
/* search bar icons */
.gb_bf button > svg,
.gb_bf.gb_cf button > svg {
	padding: 0;
	margin: 0;
	color: white;
	opacity: 1;
}
/* search bar placeholder */
 input[type="text"].gb_xf::-webkit-input-placeholder {
	color: white;
}
/* search bar dropdown */
 table.gstl_50.gssb_c {
	box-shadow: 0 0 4px rgba(0, 0, 0, .14), 0 4px 8px rgba(0, 0, 0, .28);
}
/***** BODY *****/
.nH.bkL,
.nH.bAw.nn,
.Tm.aeJ {
	background: rgb(242, 242, 242) !important;
}
/*** scrollbars ***/
.Tm.aeJ::-webkit-scrollbar,
.ajl.aib.aZ6::-webkit-scrollbar,
.Eg::-webkit-scrollbar {
	width: 6px !important;
	border: none;
}
.Tm.aeJ::-webkit-scrollbar-track,
.ajl.aib.aZ6::-webkit-scrollbar-track,
.Eg::-webkit-scrollbar-track {
	background: transparent;
}
.Tm.aeJ::-webkit-scrollbar-track-piece,
.ajl.aib.aZ6::-webkit-scrollbar-track-piece,
.Eg::-webkit-scrollbar-track-piece {
	background: transparent;
}
.Tm.aeJ::-webkit-scrollbar-thumb,
.ajl.aib.aZ6::-webkit-scrollbar-thumb,
.Eg::-webkit-scrollbar-thumb {
	border-radius: 8px;
	border: none;
	background: rgba(80, 80, 80, .5);
}
/*** left side menu ***/
.TO .nU>.n0,
.TO.NQ .nU>.n0,
.TO.nZ .nU>.n0,
.ah9>.CJ,
.n3>.CL>.CK {
	font-size: 9pt;
	color: rgb(97, 97, 97);
}
.TN.bzz:hover {
	background: rgba(97, 97, 97, .05);
	border-top-right-radius: 20px;
	border-bottom-right-radius: 20px;
}
/* compose button */
.z0>.L3,
.bhZ:not(.bym) .z0>.L3 {
	font-size: 0;
	height: 48px;
	width: 48px;
	min-width: 48px;
	padding: 0;
	position: absolute !important;
}
.z0>.L3::before {
	width: 48px;
	min-width: 48px;
}
/*** ICONS ***/

/* all */
.TO .qj {
	opacity: 1 !important;
}
.TO.NQ .qj {
	opacity: 1 !important;
}
/*** colors ***/

/*
filters calculated using:
https://stackoverflow.com/questions/42966641/how-to-transform-black-into-any-given-color-using-only-css-filters/43960991#43960991
*/

/* inbox */
.TO>.aHS-bnt .qj {
	filter: invert(49%) sepia(25%) saturate(1446%) hue-rotate(184deg) brightness(102%) contrast(79%);
}
.TO.nZ {
	background-color: rgb(91, 134, 228, .2) !important;
}
.TO .bsU {
	color: rgb(97, 97, 97);
	font-weight: normal;
}
.TO .nL.aif {
}
.TO.nZ>.aHS-bnt a {
	color: rgb(91, 134, 228) !important;
}
/* starred */
.TO>.aHS-bnw .qj {
	filter: invert(89%) sepia(6%) saturate(2885%) hue-rotate(343deg) brightness(92%) contrast(107%);
}
/* snoozed */
.TO>.aHS-bu1 .qj {
	filter: invert(60%) sepia(93%) saturate(1240%) hue-rotate(331deg) brightness(94%) contrast(90%);
}
/* important */
.TO>.aHS-bns .qj {
	filter: invert(89%) sepia(6%) saturate(2885%) hue-rotate(343deg) brightness(92%) contrast(107%);
}
/* chats */
.TO>.aHS-aHP .qj {
	filter: invert(51%) sepia(22%) saturate(1029%) hue-rotate(85deg) brightness(97%) contrast(81%);
}
/* sent */
.TO>.aHS-bnu .qj {
	filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);
}
/* drafts  */
.TO>.aHS-bnq .qj {
	filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);
}
/* all mail  */
.TO>.aHS-aHO .qj {
	/* no color shift needed since this uses inbox's icon, which is already grey */
	/*filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);*/
}
/* trash */
.TO>.aHS-bnx .qj {
	filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);
}
/* categories */
.aHS-bnr .qr {
	border-color: rgb(255, 255, 255);
}
.aHS-bnr .qj::before {
	filter: drop-shadow(1px 1px 4px #999);
}
.aHS-bnr .qj::after {
	filter: drop-shadow(3px 0 2px #bbb);
}
/* social */
.TO.aS6 .qj {
	filter: invert(41%) sepia(88%) saturate(591%) hue-rotate(321deg) brightness(82%) contrast(94%);
}
/* updates */
.TO.aS4 .qj {
	filter: invert(52%) sepia(18%) saturate(1139%) hue-rotate(217deg) brightness(96%) contrast(88%);
}
/* forums */
.TO.aS3 .qj {
	filter: invert(33%) sepia(76%) saturate(664%) hue-rotate(194deg) brightness(86%) contrast(90%);
}
/* promotions */
.TO.aS5 .qj {
	filter: invert(60%) sepia(89%) saturate(278%) hue-rotate(144deg) brightness(94%) contrast(84%);
}
/* spam */
.TO>.aHS-bnv .qj {
	filter: invert(40%) sepia(41%) saturate(842%) hue-rotate(318deg) brightness(98%) contrast(109%);
}
/*** ICONS ***/

/* snoozed */
.TO>.aHS-bu1 .qj {
	/*background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_upcoming_clr_24dp_r5.png) !important;*/
}
/* drafts */
.TO>.aHS-bnq .qj {
	background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_draft_blk_24dp_r2.png) !important;
}
/* all mail */
 .TO>.aHS-aHO .qj {
	background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_custom-cluster_24px_g60_r3.png) !important;
}
/** for retina displays **/
@media(min-resolution: 144dpi), (-webkit-min-device-pixel-ratio: 1.5) {
	/* snoozed */
	.TO>.aHS-bu1 .qj {
		/*background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_upcoming_clr_24dp_r5.png) !important;*/
	}
	/* drafts */
	.TO>.aHS-bnq .qj {
		background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_draft_blk_24dp_r2.png) !important;
	}
	/* all mail */
	.TO>.aHS-aHO .qj {
		background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_custom-cluster_24px_g60_r3.png) !important;
	}
}
/*** main section ***/

/** list area */
.Tm.aeJ {
	overflow: auto;
	padding-right: 4px;
}
/** list item **/
.zA {
	background: white;
	box-shadow: 0 -1px 0 #e0e0e0, 0 0 2px rgba(0, 0, 0, .12), 0 2px 4px rgba(0, 0, 0, .2);
	margin-left: 4px;
}
.zA span {
	font-size: 9pt;
}
/* hover */
 .zA.aqw {
	background-color: rgb(232, 234, 237) !important;
	box-shadow: 0 0 1px #aaa, 0 -1px 0 #e0e0e0, 0 0 2px rgba(0, 0, 0, .12), 0 2px 4px rgba(0, 0, 0, .2);
	z-index: 1;
}
/* read */
 .zA.yO {
}
/* unread */
 .zA.zE {
}
/* thread count */
span.bx0 {
	font-size: 6pt;
	color: rgb(255, 0, 255);
	-webkit-font-smoothing: antialiased !important;
	margin-left: 7px !important;
}
/* bottom info */
div.l2.pfiaof {
	display: none;
}
/*** right side menu ***/
.brC-aT5-aOt-Jw {
	background: transparent !important;
}

/*** hangouts window ***/
.aJn {
	z-index: 1000;    
}
}
