css:
@-moz-document url-prefix("https://www.lydsy.com/"){
body{
	background:url(https://raw.githubusercontent.com/TheKOG/repository/master/miku_.jpg)fixed center;
	background-attachment: fixed;
	background-repeat: no-repeat;
	background-size: cover;
}
div.content{
	opacity:0.8;
	background-color:#e4f0f8;
	border-radius:10px;
	padding-top:10px;
	padding-bottom:10px;
}
img{transition: all 0.2s;opacity: 1;}
img:hover{transform:scale(1.1,1.1);}
span.sampledata{background-color:#ffffff00;}
h2{
	color:#FFFFFF;
	font-size:25;
	margin-top:30px;
	margin-bottom:5px;
}
span.green{color:#FFFFFF;}
span.red{color:#FFFFFF;}
a{font-size:18;}
a:link{color:#0000FF;}
a:visited{color:#0000FF;}
a{transition:color 0.3s;}
a:hover{color:#FFFFFF;}
th{opacity: 1;}
tr.evenrow{background-color:#FFFFFFdf;}
tr.oddrow{background-color:#EEEEEEdf;}
textarea{background-color:#ffffffaa;width: 70%;}
div.notice{opacity: 0.75;}
input{opacity: 0.75;}
select{opacity: 0.75;}
center{color:#FFFFFF;}
}

