css:
@-moz-document regexp("https:\\/\\/(.*)\\.luogu\\.org([^b]*[^l]*[^o]*[^g]*)*") {
/* i really want this to be global */


body
{
   background: linear-gradient(to right, #D6A4A4, #DAE2F8); 
	
	/* 去掉注释开启背景图片 background: url(https://fancydreams.ink/wp-content/uploads/2018/09/Dandaline.jpg) no-repeat;
	background-attachment: fixed;
	background-size: 100%; */
}

#app-sidenav
{
    background: none;
}

a[data-v-3e5191a6]:hover
{
    border-radius: 10px 10px 10px 10px;
    color: #3498db;
    background: #fff;
    box-shadow: 2px 2px 2px #5e5e5e;
    transition: color 1s,background 0.45s,box-shadow 0.45s;
}

a[data-v-3e5191a6]
{
	background:none;
	color: #fff0;
	/* 去掉注释开启侧栏 （请同时开启侧菜单按钮显示）
	color: #fff;
	background: #3498db;
	box-shadow: 2px 2px 2px #5e5e5e; 
	border-radius: 10px 10px 10px 10px;
	margin: 0px 4px 4px 0px;
	*/
	filter: none !important;
}

i[data-v-7e88b118]
{
	color: #fff0;
	/* 去掉注释开启侧菜单按钮显示
	color: #0e90d2; */
	width: 4em;
	height: 3.8em;
	padding: 1.2em 0em 0em 0em;
}

div[data-v-7e88b118]
{
	background-color: #fff0 !important;
}


i[data-v-7e88b118]:hover
{
	color: #0e90d2;
}

a[data-v-7e88b118]
{
	filter: invert(70%);
}

.popup-button
{
	height: 4em;
}

.content
{
   box-shadow: 0px 3px 2px #5e5e5e;
	height: 4em !important;
	background: #fff;
}

.bread-crumb[data-v-3c5eed85]
{
	padding-top: 0;
}

.width-wrap>h1
{
	margin: 0;
}

.lg-content
{
   background: -webkit-linear-gradient(to right, #D6A4A4, #DAE2F8);  /* Chrome 10-25, Safari 5.1-6 */
   background: linear-gradient(to right, #D6A4A4, #DAE2F8) !important; /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
	margin: 0px 0px 0px 0px;
	padding: 0px 0px 0px 65px;
}

.lg-article
{
    background: #fff;
    border-radius: 15px 15px 15px 15px;
    box-shadow: 1px 1px 2px #5e5e5e;
}

.lg-summary
{
    background: #fff;
    border-radius: 15px 15px 15px 15px;
    box-shadow: 1px 1px 2px #5e5e5e;
}

.am-viewport
{
    border-radius: 5px 5px 5px 5px;
}

.am-btn
{
    border-radius: 100px 100px;
}

.am-btn:hover
{
    background-color: #ffffffd6;
    color: black;
    transition: color 1s,background 1s;
}

.lg-toolbar
{
    background: #fff0;
    border: none;
}

.am-collapse
{
    box-shadow: 2px 2px 2px #131212d9;
    background-color: white;
}

.lg-header, .lg-header-list>li
{
    background-color: white;
    color: #131212d9 !important;
}

.lg-header-li
{
    color: #131212d9 !important;
}

.am-form-field
{
    border-radius: 100px 100px 100px 100px;
}

.am-input-group-label
{
    border-radius: 100px 100px 100px 100px;
}

.lg-table-bg0
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    box-shadow: 1px 1px 2px #5e5e5e;
}

.lg-table-bg1
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    box-shadow: 1px 1px 2px #5e5e5e;
}

.lg-table-bg0 .am-progress
{
	box-shadow: none;
	border-radius: 0;
}

.am-pagination>li
{
    background: #fff;
    border-radius: 20px 20px 20px 20px;
    box-shadow: 1px 1px 2px #5e5e5e;
	margin: 4px 0px 0px 4px;
}

.am-comment-main
{
    border: 0px solid;
    background: #fff;
    border-radius: 20px 20px 20px 20px;
	box-shadow: 2px 2px 2px #9f9f9f;
}
.am-comment-main::before
{
    display: none;
}
.am-comment-main::after
{
    display: none;
}

.am-comment-hd
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
}

.am-comment-bd
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
}

.am-panel-primary
{
   background: #fff;
}

.am-pagination>li>a
{
   background: #fff0;
	border:0px solid;
	margin: 2px 2px 2px 2px;
}
.am-pagination>li>a:hover
{
   background: #fff0;
	border:0px solid;
	color: red;
}

.am-pagination .am-active>a
{
	background: #fff0;
	color: #0e90d2;
	font-weight: bold;
}

.am-pagination .am-active>a:hover
{
	background: #fff0;
	color: red;
}


.lg-sidebar-list>li
{
	margin: 5px 5px 5px 5px;
   border-radius: 20px 20px 20px 20px;
   box-shadow: 1px 1px 2px #5e5e5e;
}

.lg-sidebar-list
{
	box-shadow: none;
}

code
{
	font-family: consolas;
}

pre
{
	font-family: consolas;
}

.am-nav>li.am-active>a, .am-nav>li.am-active>a:focus, .am-nav>li.am-active>a:hover
{
	background: none;
	font-weight: bold;
	color: #0e90d2;
}

.am-nav>li>a:focus, .am-nav>li>a:hover
{
	background: none;
	color: red;
}

#app-footer, #app-header
{
	margin-left: 0em;
	background-color: #fff0;
}

.width-wrap>h1
{
	color: #1b1919d6;
	font-size: 1.5em;
}

a:hover
{
	color: #ff6464;
}

.popup[data-v-46951e14]
{
	border: none;
}

.popup-wrap::before
{
	content: "";
	position: absolute;
	top: 0px;
	left: 0px;
	width: 100%;
	height: 100%;
	background: #fff;
	border-radius: 15px 15px 15px 15px;
	box-shadow: 2px 2px 1px #5e5e5e;
	z-index: -1;
}

.tip-twitter
{
	background-color: #fff;
	border-radius: 100px 100px 100px 100px;
	color: #171616f2;
	box-shadow: 0px 0px 20px #242424;
}

.lg-record-tile
{
	border-radius: 20px 20px 20px 20px;
	box-shadow: 2px 2px 2px #5e5e5e;
}

.lg-record-tile>small:first-child
{
	text-align: center;
	padding-left: 0px;
}

.am-progress
{
	box-shadow: 2px 2px 2px #5e5e5e;
	border-radius: 100px 100px 100px 100px;
}


.lg-left .center>img
{
	border: none;
	border-color: #fff;

}
}
