css:
/**********************************/
/* Japan sytle2      v1.1.0 skin  */
/* Date: 07/04/2009               */
/* Made by: Gizzly                */
/**********************************/

@-moz-document domain("hattrick.org") {

#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
}
#footer a
{
        color: blue !important;
        font-weight: bold !important;
}
#ticker
{ 
	position: absolute !important;
        width: 350px !important;
	top: 20px !important;
	left: 430px !important;
}
#ticker div a
{
	padding-bottom: 2px !important;
	color: black !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}
#shortcuts
{
        right: 130px !important;
	margin-top: 0px !important;
	margin-left: 0px !important;
}
div#teamLinks
{
        margin-top: 15px !important;
	margin-left: 7px !important;
}
div#teamLinks a
{
        color: white !important;
}
/*--Users online - week - date - time ----------------------------*/
div[id="online"] {color: white !important;}
div[id="time"] {color: white !important;}

span.myht1 table > tr > td > a
{
       color: blue !important;
}


/*--Page background-----------------------------------------------*/
body {background: white!important;}

/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/
h1 
{
        color: blue !important;
        border-bottom: solid thin blue !important;
}
/*-----all boxHead--------------------*/
h2 
{
        color: white !important;
        background: black !important;
}
/*-----centre news--------------------*/
.boxBody h2 
{ 
        color: blue !important;
        background: none !important;
        border-bottom: solid 1px blue !important;
}
/*-----------boxHeads----------------------------------------------------------------------------------------*/
/*-------------Left menu--------------------------------------------*/
.sidebarBox .boxHead, .subMenuBox .boxHead
{
        color: blue !important;
	background: black !important;
}
div [class="boxBody"]
{
       border-left-width: 0px !important; 
       border-right-width: 0px !important;
}
div [class="boxFooter"],div [class="boxLeft"]
{
        background: white !important;
        border: 0px !important;
}

/*-------------Centre menu------------------------------------------*/
.main .boxHead h2 a
{
        color: white !important;
	background: black !important;
}
/*-------------Right menu------------------------------------------*/
.sidebarBox h2 
{
        color: white !important;
	background: black !important;
}
.boxHead sidebarBoxCollapsed.boxLeft
{
        background: black !important;
        border: 0px !important;
}

/*----- Press, GB, Team events ------------------------------------------------------------------------------*/
.main .mainBox h2 
{
	border-bottom: solid 1px blue !important;
	color: blue !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}
/*Central header paths*/
h2 a 
{
        color: blue !important;
}
h2 a:hover 
{
        color: red !important;
}
/*News and team events date*/
.date
{
	color: #808080 !important;
	padding-right: 8px;
	font-weight: 400;
}
/*Left Menu titles*/
h3 
{
        color: blue !important;
        border-bottom: solid thin blue !important;
}
/*General Links*/
a
{
        color: blue !important;
	text-decoration: none !important;
}
/*----subMenu and sidebar links------------------------------------------------------------------------------*/
.subMenuBox a, .subMenuBox a
{
        text-decoration: none !important;
	color: black !important;
}
.sidebarBox a 
{
        text-decoration: underline !important;
	color: black !important;
}
/*Bot teams link*/
div a[class="shy"] 
{
        color: gray !important;
}
/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}
/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}
/* central page background */
#content
{
        background-image: none !important;
        background-color: #0000e2 !important;
}
/*Image header (banner)*/
#header 
{
        background: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/JapaneseStyle/headerJapanBlue.jpg) !important;
	height: 102px !important;
	padding-top: 40px !important;
}
/*--------Top menu settings----------------------------------------------------------------------------------*/
#menu
{
	height: 20px !important;
	margin-top: 5px !important;
        background: blue !important;
}
#menu a
{
	line-height: 20px !important;  
        color: white !important;
}
div#menu a:hover 
{
        color: blue !important; 
        background: white !important;
}
a:hover
{
	color: red !important;
}
/*bottom white line*/
div[id="bottom"]
{
        background: blue !important;
}

/*---Aufstellung---------------------------------------------------------*/
div[class="name"] a       {color: #000000 !important;}
div[class="name"] a:hover {color: #ad1b3f !important;}

/*---Forum---------------------------------------------------------------*/
div[class="message"] a {color: blue !important;}
div[class="signature"] a {color: blue !important;}

#cfPollHeader
{
	background: black !important;
	padding: 5px 10px;
	font-weight: bold;
        color: #ffffff;
}
.cfWrapper

{
	border: solid 1px blue!important;
	margin: 0 0 12px 0;
}
.cfWrapper a
{
        color: blue !important;
}
.cfUserInfo a
{
        color: blue !important;
}
.folderHeader a
{
	color: blue !important;
	font-weight: bold !important;
}
.cfFooter a
{ 
        color: black !important;
}
.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore:hover
{

}
/*Ads section*/
div[class="insertHeader"]
{

}
div[class="insertFooter"]
{
        background: #ffffff !important;
}
/*-------------Formatting buttons----------------------------------------------------------------------------*/


/*shortcuts*/
img.scNewMail 
{

}
/*-----HT-Supporter------------------------*/
#hattrick,#hattrickNoSupporter
{
       margin: auto !important;
       position: relative;
       left: -5px;
       width: 993px !important;
}
div[class*="ad_"]
{
       display: none !important;
}

}
