}

/** /topic/[\d]+ **/

.TopicTagsContainer .TopicTag {
    margin: 0 8px 0 0;
}

/** /creator **/

.css-1kn1p8z {
    color: #fff;
    opacity: 0.9;
}

}

@-moz-document domain("zhuanlan.zhihu.com") {
/** General **/

/* tags */

body {
    color: #34495e;
    font-family: Monaco, Menlo, Microsoft Yahei;
}

h1 {
    color: #34495e;
    font-size: 24px;
    font-weight: 700;
}

h2 {
    color: #34495e;
    font-size: 18px;
    font-weight: 700;
}

h3 {
    color: #34495e;
    font-size: 16px;
    font-weight: 700;
}

.ztext blockquote {
    color: #666;
    font-size: smaller;
    font-family: Helvetica, Monaco, Menlo, Microsoft Yahei;
}

.ztext pre code {
    color: #666;
    font-size: small;
}

.ztext figure figcaption {
    font-size: small;
}

a {
    color: #ac3e40;
}

/* a:hover {
text-decoration: underline;
border-bottom: 0 !important;
} */

/* Tag */

.Tag {
    color: #175199;
    border-radius: 4px;
}

a.TopicLink {
    color: #175199;
}

/* Button */

.Button {
    color: #34495e;
}

.Button--link {
    color: #ac3e40;
}

/* 填充型的按钮 */

.Button--primary.Button--blue,
.Button--primary.Button--blue:disabled {
    background: #34495e;
    border-radius: 3px;
    border: 1px solid #4e6d8d;
    box-shadow: none;
}

/* Input */

textarea.Input {
    color: #34495e;
}

input.Input {
    color: #34495e;
}

/* App main */

.App-main {
    background-color: #f6f6f6;
}

/* Header */

.Sticky.ColumnPageHeader {
    background-color: #34495e;
    padding: 0;
    box-shadow: 0 2px 5px -1px #000;
}

/* Logo */

.ZhihuLogo {
    fill: #fff;
    fill-opacity: .9;
}

.ZhihuLogoLink svg {
    fill: #fff;
    fill-opacity: .9;
}

/* Title column */

.ColumnPageHeader-TitleColumn {
    color: #fff;
    opacity: .9;
}

.ColumnPageHeader-Button .Button--blue {
    background: #34495e;
    border-radius: 3px;
    border: 1px solid #4e6d8d;
    box-shadow: none;
    color: #fff;
    opacity: .9;
}

.ColumnPageHeader-Button .ColumnPageHeader-MenuToggler.Button--plain {
    color: #fff;
    opacity: .9;
}

.ColumnPageHeader-Button .ColumnPageHeader-MenuToggler.Button--plain:hover {
