}

/* Popover menu */

.ColumnPageHeader-Menu .Menu-item {
    color: #34495e;
}

.ColumnPageHeader-Menu .Menu-item.is-active {
    background-color: #a1c3dd;
    color: #175199;
    cursor: pointer;
}

/* Post main */

.Post-content {
    background: #f6f6f6;
}

.Post-NormalMain .Post-Header {
