    overflow: hidden;
    width: 650px;
    padding-left: 20px;
    padding-right: 20px;
    background-color: #fff;
}

.Post-NormalMain>div {
    margin: 0 auto;
    overflow: hidden !important;
    width: 650px;
    padding-left: 20px;
    padding-right: 20px;
    background-color: #fff;
}

/* ActionMenu */

.Post-SideActions button.like {
    color: #34495e;
}

.Post-SideActions button.like .Post-SideActions-icon {
    color: #34495e;
}

.Post-ActionMenu>.Menu-divider:empty {
    display: none;
}

/* PostIndex-Contributes */

.BlockTitle {
    color: #34495e;
}

/* Recommendations */

.Recommendations-Main {
    background: #F6F6F6 !important;
    margin-top: 0px !important;
}

.PagingButton svg {
    fill: #8590A6;
    ;
}

.PostItem-Title {
    color: #34495e;
}

.PostItem-Summary {
    color: #34495e;
