/* Comment */

.CommentsV2 {
    padding-bottom: 12px;
}

.Post-NormalMain .Comments-container {
    border-top: 32px solid transparent;
    padding-left: 0;
    padding-right: 0;
    width: 690px;
}

.CommentTopbar-title {
    color: #34495e;
}

.RichText a.UserLink-link {
    color: #ac3e40;
}

.CommentRichText .Richtext-content_img-square--highShape {
    width: max-content;
}

.CommentRichText .Richtext-content_img img {
    position: relative;
    left: 0;
    -webkit-transform: none;
    transform: none;
}
}
