css:
@-moz-document domain("magnumphotos.com") {
/* COLOR */

:root {
    --color_bg1: #15161a;
    --color_bg1_op90: rgba(21, 22, 26, 0.9);
    
    --color_bg2: #1f2226;
    --color_bg3: #2a2e33;
    --color_bg3_95p: rgba(42, 46, 51, 0.95);
    --color_bg4: #343a40;
    --color_bg5: #3f474d;
    --color_bg6: #495359;
    --color_bg7: #546066;
    --color_bg8: #5e6e73;
    
    --color_border1: rgba(255, 255, 255, 0.03);
    --color_border2: rgba(255, 255, 255, 0.06);
    --color_border3: rgba(255, 255, 255, 0.09);
    --color_border4: rgba(255, 255, 255, 0.12);
    --color_border5: rgba(255, 255, 255, 0.15);
    --color_border6: rgba(255, 255, 255, 0.18);
                                            
    --color_accent1: #e5e5e5;
    --color_accent1_op50p: rgba(229, 229, 229, 0.75);
    --color_accent2: #f2f2f2;
	--color_accent2_op30p: rgba(242, 242, 242, 0.3);
    --color_accent3: #ffffff;
    
    --color_fg1: #e9f2f2;
    --color_fg2: #dce5e5;
    --color_fg3: #d0d7d9;
    --color_fg4: #c4cacc;
    --color_fg5: #b8bdbf;
}


/* BACKGROUND */

.theme-teasers .teaser, .category-photographer, .swiper-wrapper, .b-caption__gallery, .rte:after, .category-photographer {
	background-color: transparent;
    background: none;
}

body, .photographer__header, .teaser, .teaser.size-hero .data.ready, .under-hero, .featured-products, .title strong, .under-hero .swiper-wrapper, .teasers .swiper-wrapper, .b-search__content, .b-search .selRow, .b-overlay__header, .theme-teasers .teaser, .swiper-wrapper, .b-caption__gallery, .b-overlay__grid, .b-overlay, .lockable, .lockable.locked {
	background-color: var(--color_bg1);
}

.search-opened .search-hider {
    background-color: var(--color_bg1_op90);
}

#main-header, #footer .teaser, .breaker-newsletter, .search-form p, .search-form button {
	background-color: var(--color_bg2);
}

#footer .related-tabs {
	background-color: var(--color_bg2) !important;
}

/* COLOR */

a, .b-caption__title, .show#mega, .show.b-overlay, .show.b-overlay__grid, .b-overlay__controls .show.grid-open, .b-overlay__controls .show.counter, .b-overlay__controls .show.grid-close, .show.b-search, .show.b-popup, .overlay-loader, .search-hider, .search-form p, .teasers, .breaker-teaser .bg, .b-form textarea, .b-form input:not([type="radio"]):not([type="checkbox"]):not([type="submit"]), .b-form input:not([type="radio"]):not([type="checkbox"]):not([type="submit"]) + label, .p-search .locked-text svg polyline, .related-tabs .tabs-nav a:hover, .related-tabs .tabs-nav a.active, .story-preview .info .text, .b-filter, .title strong, .breaker-product a, .photographer_landing__header .letter:hover {
	color: var(--color_fg1);
}

.gallery-arrow svg polyline, .b-overlay__controls .grid-open svg rect, .b-overlay__controls .grid-close svg rect, .cls-3, .b-filter .svg svg polyline {
	stroke: var(--color_fg1);
}

.b-overlay__controls .close em {
	background-color: var(--color_fg1);
}

.content-narrow .rte a, .story-quote.small .rte a, .locked .photographer__header .story-quote.photographer .rte a, .p-search__photographer .rte a {
	color: var(--color_fg3) !important;
}

.loupe-cls-3 {
	stroke: var(--color_fg3);
}

.b-caption__text, .photographer.photographer--with-image, .title {
    color: var(--color_fg3);
}

body, .rte, .photographer_landing__header p {
	color: var(--color_fg5);
}

.gallery-arrow.swiper-button-disabled svg polyline {
    stroke: var(--color_bg8);
}

/* ACCENTS */

.main-menu li.newsroom-link:hover a {
    color: #66fffc;
}

.main-menu li.arts-link:hover a, .category-arts-culture .category, .category-arts-culture .rte a, .rte .category-arts-culture a, .category-arts-culture .fs a, .fs .category-arts-culture a, .category-arts-culture .b-caption__opener {
    color: #40c6ff;
}

.main-menu li.photography-link:hover a, .category-theory-and-practice .category, .category-theory-and-practice .rte a, .rte .category-theory-and-practice a, .category-theory-and-practice .fs a, .fs .category-theory-and-practice a, .category-theory-and-practice .b-caption__opener, .main-menu li.photography-link:hover a, .category-theory-and-practice .category, .category-theory-and-practice .rte a, .rte .category-theory-and-practice a, .category-theory-and-practice .fs a, .fs .category-theory-and-practice a, .category-theory-and-practice .b-caption__opener {
    color: #6186ff;
}

.main-menu li.photographer-link:hover a, .gallery-arrow:not(.swiper-button-disabled):hover svg polyline {
    color: #66edff;
}

.main-menu li.events-link:hover a {
    color: #ff953d;
}

}

@-moz-document domain("magnumphotos.com") {
/* LAYOUT */

.rte p, .rte dl, body {
    font-size: 27px;
    line-height: 1.4em;
    letter-spacing: 0.03em;
}

.share, .header-login-dropdown-links, #mega .menu .mobile-signup a, .teaser h5, .locked .story__header .story__header__photographer, .locked .one-page__header .story__header__photographer, .b-filter li, .filtered li, .b-caption__gallery span, .b-caption__text, .b-caption__share, .tags, .author, .photographer-sidebar p, .photographer_landing__list .list_image span, .event__header .content .location, .event__counter .text, .pro-bar p {
	font-size: 15px; 
    line-height: 1.4em;
}

.button.button--signin .text, #pro p a.button--signin .text, .post-password-form input.button--signin[type='submit'] .text, .read_more, .story-quote h5, .footer-breaker .newsletter-widget form input, .teaser h4, .teaser.size-hero h5, .featured-products .info .eyebrow, .breaker-teaser h3, .story__header__subtitle, .story__header__photographer, .one-page__header__subtitle, .one-page__header__photographer, .breaker-product h3, .breaker-newsletter form input, .breaker-newsletter-expanded form input[type="text"], .breaker-newsletter-expanded form input[type="email"], .b-filter, .b-filter__column:not(.opened) .selected, .b-form .instruction, .b-form .last_line.last-check, .b-form input[type="checkbox"] + label, .b-popup__content input[type="checkbox"] + label, .page-menu a, .photographer.photographer--with-image, .license, .latest-filter a, .event__block, .p-info__intro, .p-info__intro p, .p-info a:not(.teaser) {
	font-size: 18px;
    line-height: 1.4em;
}
}

@-moz-document domain("magnumphotos.com") {
/* BORDERS */

.b-caption__gallery {
	border: none;
}

.story__footer__line, .product-teaser {
	border-color: var(--color_border4);
}

.title:before, .breakers, .breaker-product + .breaker-product {
	border-color: var(--color_border6);
}
}
