css:
@-moz-document url-prefix("https://erai-raws.info/"), url-prefix("http://erai-raws.info/"), url-prefix("https://www.erai-raws.info/"), url-prefix("http://www.erai-raws.info/") {

/* this part changes the table size */
.col-lg-9 {
	-ms-flex: 0 0 75%;
	flex: 0 0 75%;
	max-width: 100%;
}

/* this crops the image */
img {
	height: 250px;
	margin: -50px 0 -20px 0;
}
body {
	 padding:  0px
}
/* reduce padding*/
#wrapper {
	margin: 2px auto;
}

/* removes bar*/
#main > div:nth-child(2) {display:none !important; }

/* this part removes adds and messages */
#main > div:nth-child(1) {display:none !important;}
.col-xs-8 {display:none !important;}
.col-xs-4 {display:none !important;}
.col-lg-3 {display:none !important;}

/* this part changes the text color*/
.aa_ss {
	color: #fff !important;
	letter-spacing: 0px;
}

.aa_ss:hover {
	color: #aaa !important;
}
/* changes line size*/
body {	line-height: 1.2;}
/* makes links center*/
.navbar {	margin-left: 175px;}
/* change links color */
a:hover {color: #fff !important;}
a {color: rgba(255, 255, 255, 0.5)}


}

