css:
@-moz-document domain(vk.com) {
/* Удаление блока "Истории" в ленте */
.page_block.stories_feed_wrap.clear_fix.stories_feed_with_thumb.stories_feed_not_nav_buttons { display: none; }

/* Удаление блока +1 сообщения на левой панели */
.left_count_wrap.fl_r { position:absolute; top:-9999px; }

/* Удаление блока кружка уведомлений на верхней панели */
.top_notify_count { position:absolute; top:-9999px; }

/* Удаление всплывающего блока с сообщениями */
.notifier_baloon_wrap { display: none; }

/* Удаление чата справа внизу */
.chat_onl_inner { display: none; }

/* Удаление перегородки на левой панели */
.more_div.l_main { display: none; }

/* Удаление блока возможных друзей */
.friends_possible_block { display: none; }

/*Удаление "Укажите ваши контакты" */
.profile_rate_warning { display: none; }

/* Удаление блока рекомендованных приложений */
.page_block.apps_feedRightAppsBlock.apps_feedRightAppsBlock_collaborative_recommend_apps { display: none; }

/*"Что у Вас нового?" в ленте */
.wall_module.page_block.feed_post_field_wrap { display: none; }

/* Удаление блока "Актуальные фотографии" в ленте */
.feed_since.page_block{ display: none; }

/* Удаление блока "Возможные друзья" в ленте */
.page_block.feed_friends_recomm { display: none; }

.ui_toggler_wrap.hot {background: none; border-left:0px}
}
