css:
@-moz-document domain("hanzicraft.com") {
body {
  background-color: #282828;
  color: #aaa;
}

/** Top - 'Download' app */

.newsbite {
  background-color: #282828;
  border-color: #333;
  box-shadow: none;
}

#header {
  border-bottom-color: #666;
  border-bottom-style: solid;
}

/* Search box */
#input {
  border-color: #333;
}

input {
  background-color: #222;
  color: #aaa;
}

.btn:hover {
  background-color: #333;
  color: #aaa;
}

/* Tabs */
.character-tabs ol li {
  border-color: #333;
}

/*** 'Feedback' floating Pane on right ***/

#uvTab {
  background-color: #282828 !important;
  border-color: #333 !important;
}

/*** Middle Area ***/

#display {
  background-color: #282828;
  border-color: #333;
}

/* Top Character displayed */
.bigchar {
  background-color: #282828;
  border-color: #333;
}

/* 'Sign up' button */
.btn {
  background-color: #282828;
  background-image: none;
  text-shadow: none;
  box-shadow: none;
  border-color: #333;
  color: #aaa;
}

/* 'Decomposition' boxes */
.decomptitle {
  border-color: #333;
}

.decompbox {
  border-color: #333;
}

/* 'Example Words' */
.favblock, .wordblock {
  border-color: #333;
}

/* Pop-up Pane */
.popup-new {
  background-color: #282828;
  color: #aaa;
}

/* 'Appears In' */
.appearsinbox {
  border-color: #333;
}



/* Separator */
.entry {
  border-bottom-color: #666;
  border-bottom-style: solid;
}


/* Donate box */

#donate {
  background-color: #282828;
}

.btn.small-cta {
  background: #006fb7; /* #97d5fd; */
  border-color: #025e95; /* #47a6de; */
}

#info {
  background-color: #282828;
  border-color: #666;
}

.border {
  box-shadow: none;
}


/***** Chinese Character Frequency List - hanzicraft.com/lists/frequency *****/

.frequency-list .list {
  border-color: #333;
}


/***** Phonetic Sets in Chinese Characters - hanzicraft.com/lists/phonetic-sets *****/

.list_head {
  background-color: #282828;
  border-color: #333;
}

.list_details {
  border-color: #333;
}

/***** Productive Component List - hanzicraft.com/lists/productive-components *****/

/* Score */
code {
  background-color: #282828;
  border-color: #333;
  color: #b83746; /* #d14; */
  font-weight: bold;
}

/***** HanziCraft app Page - hanzicraft.com/mobile *****/

/* 'Be the first to know more' */
#mc_embed_signup {
  background-color: #282828;
}

#mc_embed_signup input.email {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

input:focus:invalid:focus, textarea:focus:invalid:focus, select:focus:invalid:focus {
  box-shadow: 0 0 6px #666; /* only color changed */
}

/* 'Subscribe' */
#mc_embed_signup .button {
  background-color: #333;
  color: #aaa;
}

#mc_embed_signup .button:hover {
  background-color: #666;
  color: #ccc;
}

/***** Login Page - hanzicraft.com/mobile *****/

.login {
  border-color: #333;
}

textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
  background-color: #282828;
  border-color: #333;
  color: #ccc;
}

/***** Sign Up Page - hanzicraft.com/signup *****/

div[style*="border: 1px solid #ddd"] {
  border-color: #333 !important;
}

/***** Dashboard on Login - hanzicraft.com/dashboard *****/

/** 'History (last 10)' **/

/* Box */
.favblockdash {
  border-color: #333;
}

/* Favorites separator */
hr {
  border-top-color: #666;
  border-bottom-color: #666;
}
}

@-moz-document regexp(".*www.herokucdn.com/error-pages/application-error.html") {
html, body {
  background-color: #282828;
}

/* ' Application error' */
.message__title {
  color: #aaa;
}
}
