css:
@-moz-document regexp("^https?\:\/\/.*$") {

    :root {
        scrollbar-face-color: /*[[face-color]]*/ !important; 
        scrollbar-track-color: /*[[track-color]]*/ !important; 
  
        scrollbar-color: /*[[face-color]]*/ /*[[track-color]]*/ !important;
  
        scrollbar-width: /*[[s-width]]*/ !important;
    }

    select:not([multiple]), select:not([size]) {
       scrollbar-width: auto !important;
    }

     /*[[inherit]]*/

}
@-moz-document url-prefix("https://userstyles.org/styles/167361/c") {
/* Self-promo */
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w),
div.recommendations:not(#krystian3w) {
  display: none !important;
  visibility: hidden !important;
  opacity: 0 !important;
  filter: opacity(0) !important;
  user-select: none !important;
  pointer-events: none !important;
  width: 0 !important;
  height: 0 !important;
}
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w) * {
  width: 0 !important;
  height: 0 !important;
}
}
